class DataSourceContent{
    static let GOM_GYAB_DOT_TANG_CONTENT: String = "༡། རྐང་པ་རྡོ་རྗེ་དཀྱིལ་ཀྲུང་།\n༢། ལག་པ་མཉམ་བཞག་གི་ཕྱག་རྒྱ། གཡས་པ་སྟེང་དང་གཡོན་པ་འོག་ཏུ་བྱས། ། \n༣། སྐེད་པ་དྲང་པོར་བསྲངས་པ་དང་། སྒལ་ཚིགས་ཐད་ཀར་འོང་བར་བྱ།\n ༤། སོ་མཆུ་རང་བབས་སུ་བཞག\n༥། ལྕེ་རྩེ་རྐན་ལ་སྦྱར། \n༦། མིག་སྣ་རྩེ་ལ་ཕབ། ༧། ཕྲག་པ་གཉིས་ཐད་ཀར་འཇོག་\n༨། དབུགས་འགྲོ་འོང་རང་བབས་བཞག";
         static let KULONG_JAY_TANG_CONTENT: String = "༡། མཐའ་ཡས་པའི་མ་རྒན་སེམས་ཅན་གྱི་དོན་དུ་སངས་རྒྱས་ཀྱི་གོ་འཕང་འཐོབ་པར་བྱ། དེའི་ཕྱིར་དུ་གཞན་དོན་དང་སློབ་སྦྱོང་སོགས་ཡག་པོ་བྱ་བསམ་པའི་ཀུན་སློང་གཙང་མ་འཛིན་དགོས། \n" +
                 "༢། དེ་རིང་གི་ཉིན་མོར་བྱ་བ་གང་བྱས་པ་ཐམས་ཅད་གཞན་ལ་ཕན་པའི་རྒྱུ་རུ་འགྱུར་བར་ཤོག་བསམ་པ་དགོས། \n" +
                 " ༣། རྗེ་བཙུན་འཇམ་དཔལ་དབྱངས་དང་ྋགོང་ས་ྋསྐྱབས་མགོན་ཆེན་པོ་མཆོག་གིས་སེམས་ཅན་གྱི་དོན་ཇི་ལྟར་མཛད་པ་བཞིན་ངས་ཀྱང་དེ་ལྟར་བྱེད་ཐུབ་པར་ཤོག་\n" +
                 "༤། འགྲོ་བ་ཇི་སྲིད་དུ་གནས་པ་དེ་སྲིད་དུ་ང་ཡང་གནས་ཐུབ་པ་དང་། ང་གནས་པ་དེ་སྲིད་དུ་འགྲོ་བའི་སྡུག་བསྔལ་སེལ་ཐུབ་པ་ཡོང་བར་ཤོག་ཅེས་བསམ་དགོས། མདོར་ན་བདག་ཅག་གི་སྟོན་པ་ཤཀྱཱ་ཐུབ་པ་དང་ྋགོང་ས་ྋསྐྱབས་མགོན་ཆེན་པོ་མཆོག་གི་དགོངས་པ་དང་མཐུན་པར་ཡོང་བར་ཤོག ཡོང་བར་བྱེད་སྙམ་པ་དེ་ཡིན།།";
         static let GYUN_CHAK_SUM_CONTENT: String = "༄༅། །སྟོ ན་པ་བཅོམ་ལྡན་འདས་དེ་བཞིན་གཤེགས་པ་དགྲ་བཅོམ་པ་ཡང་དག་པ་རྫོགས་པའི་སངས་རྒྱས་རིག་པ་དང་ཞབས་སུ་ལྡན་པ། བདེ་ས་བར་གཤེགས་པ་འཇིག་རྟེན་མཁྱེན་པ། སྐྱེས་བུ་འདུལ་བའི་ཁ་ལོ་བསྒྱུར་བ། བླ་ན་མེད་པ། ལྷ་དང་མི་རྣམས་ཀྱི་སྟོན་པ། སངས་རྒྱས་བཅོམ་ལྡན་འདས་དཔལ་རྒྱལ་བ་ཤཱཀྱ་ཐུབ་པ་ལ་ཕྱག་འཚལ་ལོ། མཆོད་དོ་སྐྱབས་སུ་མཆིའོ། གང་ཚེ་རྐང་གཉིས་གཙོ་བོ་ཁྱོད་བལྟམས་ཚེ། ས་ཆེན་འདི་ལ་གོམ་པ་བདུན་བོར་ནས། ང་ནི་འཇིག་རྟེན་འདི་ན་མཆོག་ཅེས་གསུངས། དེ་ཚེ་མཁས་པ་ཁྱོད་ལ་ཕྱག་འཚལ་ལོ། རྣམ་དག་སྐུ་མངའ་མཆོག་ཏུ་གཟུགས་བཟང་བ། ཡེ་ཤེས་རྒྱ་མཚོ་གསེར་གྱི་ལྷུན་པོ་འདྲ། གྲགས་པ་འཇིག་རྟེན་གསུམ་ན་ལྷམ་མེ་བ། མགོན་པོ་མཆོག་བརྙེས་ཁྱོད་ལ་ཕྱག་འཚལ་ལོ། མཚན་མཆོག་ལྡན་པ་དྲི་མེད་ཟླ་བའི་ཞལ། གསེར་མདོག་འདྲ་བ་ཁྱོད་ལ་ཕྱག་འཚལ་ལོ། རྡུལ་བྲལ་ཁྱོད་འདྲ་སྲིད་པ་གསུམ་མ་མཆིས། མཉམ་མེད་མཁྱེན་ཅན་ཁྱོད་ལ་ཕྱག་འཚལ་ལོ། མགོན་པོ་ཐུགས་རྗེ་ཆེ་ལྡན་པ། ཐམས་ཅད་མཁྱེན་པས་སྟོན་པ་པོ། བསོད་ནམས་ཡོན་ཏན་རྒྱ་མཚོའི་ཞིང་། དེ་བཞིན་གཤེགས་ལ་ཕྱག་འཚལ་ལོ། དག་པས་འདོད་ཆགས་བྲལ་བར་གྱུར། དགེ་བས་ངན་སོང་ལས་གྲོལ་ཞིང་། གཅིག་ཏུ་དོན་དམ་མཆོག་གྱུར་པ། ཞི་གྱུར་ཆོས་ལ་ཕྱག་འཚལ་ལོ། གྲོལ་ནས་གྲོལ་བའི་ལམ་ཡང་སྟོན། བསླབ་པ་དག་ལ་རབ་དུ་གནས། ཞིང་གི་དམ་པ་ཡོན་ཏན་ལྡན། དགེ་འདུན་ལ་ཡང་ཕྱག་འཚལ་ལོ། སངས་རྒྱས་གཙོ་ལ་ཕྱག་འཚལ་ལོ། སྐྱོབ་པ་ཆོས་ལ་ཕྱག་འཚལ་ལོ། དགེ་འདུན་ཆེ་ལ་ཕྱག་འཚལ་ལོ། གསུམ་ལ་རྟག་ཏུ་གུས་ཕྱག་འཚལ། ཕྱག་བྱར་འོས་པ་ཐམས་ཅད་ལ། ཞིང་རྡུལ་ཀུན་གྱིས་གྲངས་སྙེད་ཀྱི། ལུས་བཏུད་པ་ཡིས་རྣམ་ཀུན་ཏུ། མཆོག་ཏུ་དད་པས་ཕྱག་འཚལ་ལོ། སྐར་མ་རབ་རིབ་མར་མེ་དང་། སྒྱུ་མ་ཟིལ་བ་ཆུ་བུར་དང་། རྨི་ལམ་གློག་དང་སྤྲིན་ལྟ་བུ། འདུས་བྱས་ཆོས་རྣམས་དེ་ལྟ ར་ལྟ། བསོད་ནམས་འདི་ཡིས་ཐམས་ཅད་གཟིགས་པ་ཡི། གོ་འཕང་ཐོབ་ནས་སྐྱོན་གྱི་དགྲ་འདུལ་ཏེ། རྒ་དང་ན་དང་འཆི་བའི་རླབས་འཁྲུགས་པའི། སྲིད་པའི་མཚོ་ལས་འགྲོ་བ་\nསྒྲོལ་བར་ཤོག །།";
         static let KYAB_DO_SAM_KAY_CONTENT: String = "༄༅། །ཕྱོགས་བཅུ་དུས་གསུམ་གྱི་དེ་བཞིན་གཤེགས་པ་ཐམས་ཅད་ཀྱི་སྐུ་གསུང་ཐུགས་ཡོན་ཏན་འཕྲིན་ལས་ཐམས་ཅད་ཀྱི་ངོ་བོར་གྱུར་པ། ཆོས་ཀྱི་ཕུང་པོ་སྟོང་ཕྲག་བརྒྱད་ཅུ་རྩ་བཞིའི་འབྱུང་གནས། འཕགས་པའི་དགེ་འདུན་ཐམས་ཅད་ཀྱི་མངའ་བདག དྲིན་ཅན་རྩ་བ་དང་བརྒྱུད་པར་བཅས་པའི་དཔལ་ལྡན་བླ་མ་དམ་པ་རྣམས་ལ་སྐྱབས་སུ་མཆིའོ། བླ་མ་ལ་སྐྱབས་སུ་མཆིའོ། སངས་རྒྱས་ལ་སྐྱབས་སུ་མཆིའོ། ཆོས་ལ་སྐྱབས་སུ་མཆིའོ། དགེ་འདུན་ལ་སྐྱབས་སུ་མཆིའོ། (ལན་གསུམ་བརྗོད།) ཡི་དམ་དཀྱིལ་འཁོར་གྱི་ལྷ་ཚོགས་འཁོར་དང་བཅས་པ་རྣམས་ལ་སྐྱབས་སུ་མཆིའོ། དཔལ་མགོན་དམ་པ་ཆོས་སྐྱོང་བའི་སྲུང་མ་ཡེ་ཤེས་ཀྱི་སྤྱན་དང་ལྡན་པ་རྣམས་ལ་སྐྱབས་སུ་མཆིའོ། སངས་རྒྱས་ཆོས་དང་ཚོགས་གྱི་མཆོག་རྣམས་ལ། བྱང་ཆུབ་བར་དུ་བདག་ནི་སྐྱབས་སུ་མཆིའོ། བདག་གིས་སྦྱིན་སོགས་བགྱིས་པའི་ཚོགས་རྣམས་ཀྱིས། འགྲོ་ལ་ཕན་ཕྱིར་སངས་ཪྒྱས་འགྲུབ་པར་ཤོག (ལན་གསུམ་བརྗོད།) དཀོན་མཆོག་གསུམ་ལ་བདག་སྐྱབས་མཆི། སྡིག་པ་ཐམས་ཅད་སོ་སོར་བཤགས། འགྲོ་བའི་དགེ་ལ་རརྗེས་ཡི་རང་སངས་རྒྱས་བྱང་ཆུབ་ཡིད་ཀྱིས་གཟུང་། སངས་རྒྱས་ཆོས་དང་ཚོགས་མཆོག་ལ། བྱང་ཆུབ་བར་དུ་བདག་སྐྱབས་མཆི། རང་གཞན་དོན་ནི་རབ་སྒྲུབ་ཕྱིར། བྱང་ཆུབ་སེམས་ནི་བསྐྱེད་པར་བགྱི། བྱང་ཆུབ་མཆོག་གི་སེམས་ནི་བསྐྱེད་བགྱིས་ནས། སེམས་ཅན་ཐམས་ཅད་བདག་གིས་མགྲོན་དུ་གཉེར། བྱང་ཆུབ་སྤྱོད་མཆོག་ཡིད་འོང་སྤྱད་པར་བགྱི། འགྲོ་ལ་ཕན་ཕྱིར་སངས་རྒྱས་འགྲུབ་པར་ཤོག སེམས་ཅན་ཐམས་ཅད་བདེ་བ་དང་བདེ་བའི་རྒྱུ་དང་ལྡན་པར་གྱུར་ཅིག སེམས་ཅན་ཐམས་པར་ཅད་སྡུག་བསྔལ་དང་སྡུག་བསྔལ་གྱི་རྒྱུ་དང་བྲལ་བར་གྱུར་ཅིག་སེམས་ཅན་ཐམས་ཅད་སྡུག་བསྔལ་མེད་པའི་བདེ་བ་དང་མི་འབྲལ་བར་གྱུར་ཅིག་སེམས་ཅན་ཐམས་ཅད་ཉེ་རིང་ཆགས་སྡང་གཉིས་དང་བྲལ་བའི་བཏང་སྙོམས་ལ་གནས་པར་གྱུར་ཅིག (ལན་གསུམ་བརྗོད།) གང་གིས་རྟེན་ཅིང་འབྲེལ་བར་འབྱུང་། །འགག་པ་མེད་པ་སྐྱེ་མེད་པ། །ཆད་པ་མེད་པ་རྟག་མེད་པ། །འོང་བ་མེད་པ་འགྲོ་མེད་པ། །ཐ་དད་དོན་མིན་དོན་གཅིག་མིན། ། སྤྲོས་པ་ཉེར་ཞི་ཞི་བསྟན་པ། །རྫོགས་པའི་སངས་རྒྱས་སྨྲ་རྣམས་ཀྱི། །དམ་པ་དེ་ལ་ཕྱག་འཚལ་ལོ། ཉན་ཐོས་ཞི་བ་འཚོལ་རྣམས་ཀུན་ཤེས་ཉིད་ཀྱིས་ཉེར་ཞིར་འཁྲིད་མཛད་གང ཡིན་དང་། འགྲོ་ལ་ཕན་པར་བྱེད་རྣམས་ལམ་ཤེས་ཉིད་ཀྱིས་འཇིག་རྟེན་དོན་སྒྲུབ་མཛད་པ་གང་། གང་དང་ཡང་དག་ལྡན་པས་ཐུབ་རྣམས་རྣམ་པ་ཀུན་ལྡན་སྣ་ཚོགས་འདི་གསུངས་པ། ཉན་ཐོས་བྱང་ཆུབ་སེམས་དཔའི་ཚོགས་བཅས་སངས་རྒྱས་ཀྱི་ནི་ཡུམ་དེ་ལ་ཕྱག་འཚལ་ལོ། རྟོག་པའི་དྲ་བ་རྣམ་བསལ་ཅིང་། །ཟབ་ཅིང་རྒྱ་ཆེའི་སྐུ་མངའ་བ། །ཀུནཏུ་བཟང་པོའི་འོད་ཟེར་དག །ཀུན་ནས་འཕྲོ་ལ་ཕྱག་འཚལ་ལོ། ";
         static let YANLAK_DUNPA_CONTENT: String = "༄༅། ། ཇི་སྙེད་སུ་དག་ཕྱོགས་བཅུའི་འཇིག་རྟེན་ན། །དུས་གསུམ་གཤེགས་པ་མི་ཡི་སེངྒེ་ཀུན། །བདག་གིས་མ་ལུས་དེ་དག་ཐམས་ཅད་ལ། །ལུས་དང་ངག་ཡིད་དང་བས་ཕྱག་བགྱིའོ། །བཟང་པོ་སྤྱོད་པའི་སྨོན་ལམ་སྟོབས་དག་གིས། །རྒྱལ་བ་ཐམས་ཅད་ཡིད་ཀྱི་མངོན་སུམ་དུ། །ཞིང་གི་རྡུལ་སྙེད་ལུས་རབ་བཏུད་པ་ཡིས། །རྒྱལ་བ་ཀུན་ལ་རབ་ཏུ་ཕྱག་འཚལ་ལོ། །རྡུལ་གཅིག་སྟེང་ན་རྡུལ་སྙེད་སངས་རྒྱས་རྣམས། །སངས་རྒྱས་སྲས་ཀྱི་དབུས་ན་བཞུགས་པ་དག །དེ་ལྟར་ཆོས་ཀྱི་དབྱིངས་རྣམས་མ་ལུས་པ། །ཐམས་ཅད་རྒྱལ་བ་དག་གིས་གང་བར་མོས། །དེ་དག་བསྔགས་པ་མི་ཟད་རྒྱ་མཚོ་རྣམས། །དབྱངས་ཀྱི་ཡན་ལག་རྒྱ་མཚོའི་སྒྲ་ཀུན་གྱིས། །རྒྱལ་བ་ཀུན་གྱི་ཡོན་ཏན་རབ་བརྗོད་ཅིང་། བདེ་བར་གཤེགས་པ་ཐམས་ཅད་བདག་གིས་བསྟོད། །མེ་ཏོག་དམ་པ་ཕྲེང་བ་དམ་པ་དང་། །སིལ་སྙན་རྣམས་དང་བྱུག་པ་གདུགས་མཆོག་དང་། །མར་མེ་མཆོག་དང་བདུག་སྤོས་དམ་པ་ཡིས། །རྒྱལ་བ་དེ་དག་ལ་ནི་མཆོད་པར་བགྱི། ན་བཟའ་དམ་པ་རྣམས་དང་དྲི་མཆོག་དང་། །ཕྱེ་མ་ཕུར་མ་རི་རབ་མཉམ་པ་དང་། །བཀོད་པ་ཁྱད་པར་འཕགས་པའི་མཆོག་ཀུན་གྱིས། །རྒྱལ་བ་དེ་དག་ལ་ནི་མཆོད་པར་བགྱི། །མཆོད་པ་གང་རྣམས་བླ་མེད་རྒྱ་ཆེ་བ། །དེ་དག་རྒྱལ་བ་ཐམས་ཅད་ལ་ཡང་མོས། །བཟང་པོ་སྤྱོད་ལ་དད་པའི་སྟོབས་དག་གིས། །རྒྱལ་བ་ཀུན་ལ་ཕྱག་འཚལ་མཆོད་པར་བགྱི། །འདོད་ཆགས་ཞེ་སྡང་གཏི་མུག་དབང་གིས་ནི། །ལུས་དང་ངག་དང་དེ་བཞིན་ཡིད་ཀྱིས་ཀྱང་། །སྡིག་པ་བདག་གིས་བགྱིས་པ་ཅི་མཆིས་པ། །དེ་དག་ཐམས་ཅད་བདག་གིས་སོ་སོར་བཤགས། །ཕྱོགས་བཅུའི་རྒྱལ་བ་ཀུན་དང་སངས་རྒྱས་སྲས། །རང་རྒྱལ་རྣམས་དང་སློབ་དང་མི་སློབ་དང་། །འགྲོ་བ་ཀུན་གྱི་བསོད་ནམས་གང་ལ་ཡང་། །དེ་དག་ཀུན་གྱི་རྗེས་སུ་བདག་ཡི་རང་། །གང་རྣམས་ཕྱོགས་བཅུའི་འཇིག་རྟེན་སྒྲོན་མ་དག བྱང་ཆུབ་རིམ་པར་སངས་རྒྱས་མ་ཆགས་བརྙེས། །མགོན་པོ་དེ་དག་བདག་གིས་ཐམས་ཅད་ལ། །འཁོར་ལོ་བླ་ན་མེད་པ་བསྐོར་བ་བསྐུལ། །མྱ་ངན་འདའ་སྟོན་གང་བཞེད་དེ་དག་ལ། །འགྲོ་བ་ཀུན་ལ་ཕན་ཞིང་བདེ་བའི་ཕྱིར། །བསྐལ་པ་ཞིང་གི་རྡུལ་སྙེད་བཞུགས་པར་ཡང་། །བདག་གིས་ཐལ་མོ་རབ་སྦྱར་གསོལ་བར་བགྱི། །ཕྱག་འཚལ་བ་དང་མཆོད་ཅིང་བཤགས་པ་དང་། །རྗེས་སུ་ཡི་རང་བསྐུལ་ཞིང་གསོལ་བ་ཡི། །དགེ་བ་ཅུང་ཟད་བདག་གིས་ཅི་བསགས་པ། །ཐམས་ཅད་བདག་གིས་བྱང་ཆུབ་ཕྱིར་བསྔོའོ། ";
         static let KAP_SUMPA_CONTENT: String = "༈ སྐབས་གསུམ་པ་དང་དབང་པོའི་སྒྲ། དྲི་ཟ་གདེངས་ཅན་གྲུབ་པའི་གཙོ། ཀུན་གྱི་གཙུག་རྒྱན་ནོར་བུ་ཡིས། གང་གི་ཞབས་པད་མཛེས་བྱས་པ། གསེར་འོད་འཁྱུག་པའི་དཔལ་གྱིས་བརྗིད་པའི་སྐུ། དྲི་ཟའི་དབྱངས་ཀྱིས་བསྒྲུན་དུ་མེད་པའི་གསུང་། ཉི་མ་བྱེ་བའི་མདངས་ལས་གསལ་བའི་ཐུགས། མཐའ་ཡས་འགྲོ་བའི་འདྲེན་མཆོག་ཤཱཀྱའི་ཏོག དག་པའི་མཁའ་ལ་ཤར་བའི་གཟའ་སྐར་རྣམས། ཆུ་གཏེར་དབུས་ན་ཡོངས་སུ་གསལ་བ་བཞིན། དང་བའི་ཡིད་མཚོར་ཁྱོད་ཀྱི་ཡོན་ཏན་གྱི། གཟུགས་བརྙན་མ་འདྲེས་གསལ་བར་རབ་བཀོད་ནས། ཟླ་ཟེར་ཕོག་པའི་ཆུ་སྐྱེས་གཞོན་ནུ་བཞིན། ལག་པའི་སོར་མོ་སྙིང་གའི་ངོས་སུ་ཟུམ། རྩེ་གཅིག་ཡིད་ཀྱིས་ཁྱོད་སྐུར་མངོན་ཕྱོགས་ཏེ། ཅུང་ཟད་བསྟོད་ལ་ཐུགས་རྗེ་ཅན་ཁྱོད་དགོངས། སྐར་མའི་ཚོགས་ཀྱིས་མཁའ་ལ་ཟླ་བ་བཞིན། རྒྱལ་སྲས་དཔའ་བོ་རྣམས་ཀྱིས་ཀུན་ནས་བསྐོར། བ་ལང་ཁྱུ་ཡི་ཐོག་མར་ཁྱུ་མཆོག་བཞིན། དགྲ་བཅོམ་འཁོར་གྱི་ཚོགས་ཀྱིས་མདུན་དུ་བདར། ནམ་མཁའི་ཁྱོན་ཀུན་འགེངས་པའི་འོད་ཟེར་གྱི། དྲ་བ་སྟོང་གིས་ཡོངས་སུ་དཀྲིགས་པའི་སྐུ། ནམ་མཁའི་ལམ་ནས་ཆོ་འཕྲུལ་དུ་མ་ཡིས། ངང་པའི་རྒྱལ་པོ་བཞིན་དུ་ལྡིང་ཞིང་གཤེགས། དེ་ཚེ་རིང་ནས་བསྐྲུན་པའི་བཟང་པོའི་ལས། ཡོངས་སུ་སད་པའི་ལྷ་མོ་སྟོང་ཕྲག་བརྒྱ། ཁྱོད་ཞལ་མཐོང་བའི་མོད་ལ་མི་བསྲུན་ཡིད། ཉེས་བརྒྱའི་འཆིང་བ་ཀུན་ལས་ལྷོད་པར་གྱུར། དཔའ་བོ་ཁྱོད་ཀྱིས་བྱང་ཆུབ་ཤིང་དྲུང་དུ། མཁྱེན་བརྩེའི་དཔུང་གིས་ཡིད་སྲུབས་བདག་པོའི་སྡེ། མ་ལུས་ཟིལ་གྱི་གནོན་པར་མཛད་པ་ནི། དྲག་པོའི་རླུང་དང་ཉེ་བའི་སྤྲིན་ནག་བཞིན། མདའ་མཚོན་གོ་ཆ་མ་བཟུང་བར། རང་ཉིད་གཅིག་པུས་བྱེ་བའི་བདུད། མ་ལུས་ཕམ་མཛད་གཡུལ་གྱི་ལས། ཁྱོད་ལས་གཞན་པ་སུ་ཡིས་ཤེས། དེ་ལྟར་ཁྱོད་ཀྱི་བྱམས་པའི་མེས། འདོད་ལྷའི་སྙིང་ནི་རབ་གདུངས་ཀྱང་། བརྩེ་བའི་གཏེར་ཁྱོད་ལུས་ཅན་ལ། རིས་སུ་ཆད་པར་གྱུར་པ་མེད། ཁྱོད་ནི་འགྲོ་བའི་དོན་གྱི་སླད། ཅུང་ཟད་ཙམ་ཡང་མི་ངལ་ཞིང་། འགྲོ་བ་རྣམས་ཀྱང་ཁྱོད་ཀྱི་ནི། ཡོན་ཏན་བརྗོད་ལ་ངལ་མ་གྱུར། རྒྱ་མཚོའི་ཀླུང་ལྟར་རབ་ཏུ་ཟབ་པའི་ཐུགས། ལྷ་ཡི་རྔ་བཞིན་ལེཌ་པར་འདོམས་པའི་གསུང་།ལྷུན་པའི་སྤོ་ལྟར་མངོན་པར་འཕགས་པའི་སྐུ། མཐོང་ཐོས་དྲན་པ་དོན་ལྡན་མཛད་པ་ཁྱོད། འཇིག་རྟེན་ཀུན་གྱི་སེམས་ཅན་ཐམས་ཅད་ཀྱིས། དུས་གཅིག་ཉིད་དུ་ཐེ་ཚོམ་དོན་ཞུས་ཀྱང་། དེ་དག་རེ་རེའི་མདུན་དུའང་དེ་སྙེད་ཀྱི། སྐུ་དང་གསུང་གི་བཀོད་པ་དུས་གཅིག་ཏུ། མཛད་པ་མཛད་ཀྱང་རྟོག་པ་ཉེར་ཞི་བ། ཁྱོད་ཀྱི་སྐུ་གསུང་ཐུགས་ཀྱི་གསང་བ་ཡི། ཚུལ་འདི་རྒྱས་སྲས་རང་རྒྱལ་ཉན་ཐོས་ཀྱིས། ཇི་ཙམ་བརྟགས་ཀྱང་བློ་ཡི་ཡུལ་མིན་ན། ཚངས་དང་དབང་པོ་སོགས་ཀྱིས་སྨོས་ཅི་འཚལ། མཁའ་ལྡིང་དབང་པོས་མཁའ་ལ་བགྲོད་བགྲོད་ནས། ནམ་ཞིག་རང་སྟོབས་བྲི་བས་ལྡོག་འགྱུར་གྱི། ནམ་མཁའ་ཟད་པས་ལྡོག་པར་མི་འགྱུར་བཞིན། ཁྱོད་ཀྱིས་ཡོན་ཏན་བརྗོད་པའང་དེ་ལྟར་ལགས། གཤོག་རྩལ་རབ་ཏུ་རྫོགས་པའི་མཁའ་ལྡིང་གིས། བགྲོད་པའི་ལམ་དུ་བྱིའུས་རྩོལ་བ་ལྟར། སྟོབས་ལམ་བཅུ་མངའ་བ་གང་དུ་གཤེགས་པ་ཡི། ལམ་མཆོག་དེར་ནི་འཇུག་པར་འདོད་པ་བདག གནས་ལུགས་དོན་ལ་ལྟ་བའི་བློ་མིག་ཉམས། ངེས་འབྱུང་བྱང་ཆུབ་སེམས་ཀྱི་འབྱོར་བས་དབུལ། སྟོབས་ལྡན་ཉོན་མོངས་དགྲ་བོས་རྒྱུན་དུ་གཅེས། བདག་འཇིན་གཅོང་རོང་ཟབ་མོའི་སྦུབས་སུ་ལྷུང༌། འདི་འདྲའི་ཉམ་ཐག་གནས་སུ་གྱུར་ལགས་པ། བརྩེ་ཆེན་ཁྱོད་ཀྱིས་ཡལ་བར་འདོར་ལགས་ན། དམན་པ་རྣམས་ལ་ཆེས་ཆེར་བརྩེ་ར བའི་མགོན། ཁྱོད་ལས་གཞན་པ་གང་ལ་སྐྱབས་སུ་བཟུང་། སྙིགས་མ་ལྔ་ཡིས་ཧ་ཅང་སྦགས་པ་ཡི། ཞིང་འདི་གཞན་གྱིས་སྤངས་དུས་མགོན་ཁྱོད་ཀྱིས། ཡོངས་སུ་བཟུང་སྟེ་རྒྱལ་བ་རྒྱལ་སྲས་ཀྱིས། པད་དཀར་ལྟ་བུར་བསྔགས་པའི་དགོངས་པའང་ཅི། འོན་ཀྱང་ཁྱོད་ཀྱི་ཕྲིན་ལས་གདུལ་བྱ་ལ། མ་བརྟགས་བཏང་སྙོམས་ཅུང་ཟད་མི་མངའ་བས། སྐལ་ངན་བདག་གི་ཉེས་པ་ཁོ་ནར་ཟད། དེ་ཕྱིར་ཁྱོད་ལ་སྐྱོན་གྱི་སྐབས་མེད་པས། དེང་ནས་བྱང་ཆུབ་སྙིང་པོ་བདུད་སྡེ་ལས། ཇི་སྲིད་རྒྱལ་བར་མ་གྱུར་དེ་སྲིད་དུ། སྐྱེ་ཞིང་སྐྱེ་བར་ཁྱོད་ཀྱིས་རྗེས་བཟུང་ནས། གསུང་གི་བདུད་རྩིས་ངོམས་པ་མེད་པར་ཤོག དེ་བཞིན་གཤེགས་པ་ཁྱེད་སྐུ་ཅི་འདྲ་དང་། འཁོར་དང་སྐུ་ཚེའི་ཚད་དང་ཞིང་ཁམས་སོགས། ཁྱོད་ཀྱི་མཚན་མཆོག་བཟང་པོ་ཅི་འདྲ་བ། དེ་འདྲ་ཁོ་ནར་བདག་སོགས་འགྱུར་བར་ཤོག ཁྱོད་ལ་བསྟོད་ཅིང་གསོལ་བ་བཏབ་པའི་མཐུས། བདག་སོགས་གང་དུ་གནས་པའི་ས་ཕྱོགས་དེར། ནད་གདོན་དབུལ་འཕོངས་འཐབ་རྩོད་ཞི་བ་དང་། ཆོས་དང་བཀྲ་ཤིས་འཕེལ་བར་མཛད་དུ་གསོལ། ";
         static let KHA_NYAM_MA_CONTENT: String = "༈ མཁའ་མཉམ་འགྲོ་ལ་མཁྱེན་བརྩེས་རབ་དགོངས་ནས། གྲངས་མེད་གསུམ་དུ་ཚོགས་གཉིས་རབ་རྫོགས་ཤིང་། སྟོང་གསུམ་མི་མཇེད་ཞིང་གི་དཔལ་གྱུར་པ། འདྲེན་མཆོག་ཤཱཀྱའི་ཏོག་ལ་ཕྱག་འཚལ་ལོ། ཤེས་རབ་ཆེན་པོས་དེ་ཉིད་ལེགས་རྟོགས་ནས། བརྩོན་པ་ཆེན་པོས་གོ་འཕང་མཆོག་བརྙེས་པ། བསྟན་པ་འདི་ལ་སྟོན་པ་གཉིས་པར་གྲགས། འཕགས་མཆོག་ཀླུ་སྒྲུབ་ཞབས་ལ་ཕྱག་འཚལ་ལོ། དེ་སྲས་ཐུ་བོ་དགེ་ལེགས་དཔལ་ཡོན་ཅན། ལོག་སྨྲ་ཚར་བཅད་སྐལ་ལྡན་རྗེས་སུ་འཛིན། ཐར་འདོད་རྣམས་ལ་ཐར་པའི་ལམ་སྟོན་པ། ཨཱརྱ་དེ་ཝའི་ཞབས་ལ་ཕྱག་འཚལ་ལོ། མཁྱེན་པའི་དཀྱིལ་འཁོར་ལེགས་བཤད་འོད་སྟོང་ཅན། རབ་འབྱམས་ཤེས་བྱའི་མཁའ་ལ་རབ་འཕགས་ཤིང༌། བདེ་གཤེགས་གསུང་རབ་ཉིན་མོར་འབྱེད་པའི་དཔལ། འཕགས་པ་ཐོགས་མེད་ཞབས་ལ་ཕྱག་འཚལ་ལོ། དགུ་བཅུ་གོ་དགུ་འབུམ་སྡེའི་གཞུང་རྣམས་ཀྱིས། གང་གི་བློ་གྲོས་མགུལ་བ་རབ་བརྒྱན་ཅིང་། འཛམ་གླིང་འདི་ན་ཀུན་མཁྱེན་གཉིས་པར་གྲགས། མཁས་མཆོག་དབྱིག་གཉེན་ཞབས་ལ་ཕྱག་འཚལ་ལོ། ཕུལ་ན་ཞ དུ་བྱུང་བའི་བློ་གྲོས་སྟོབས་ཀྱིས་བཙུན། རྨད་དུ་བྱུང་བའི་ཡོན་ཏན་ཁུར་གྱིས་བརྗིད། ལོག་སྨྲ་འདམ་བུའི་ཚལ་རབ་འཇོམས་མཛད་པའི། ཕྱོགས་ཀྱི་གླང་པོའི་ཞབས་ལ་ཕྱག་འཚལ་ལོ། ཆོས་ཀྱི་རིགས་པས་ཆོས་མིན་ཕམ་མཛད་ཅིང་། གྲགས་པའི་སྒྲ་ཆེན་འཇིག་རྟེན་གསུམ་ན་གྲགས། སྨྲ་ངན་མུན་འཇོམས་སྨྲ་བའི་ཉི་མ་མཆོག ཆོས་ཀྱི་གྲགས་པའི་ཞབས་ལ་ཕྱག་འཚལ་ལོ། ཚུལ་ཁྲིམས་རྒྱ་མཚོའི་དབུས་ན་གནས་བཅས་ཤིང་། འབུམ་ཕྲག་འདུལ་བའི་གཞུང་གི་གདེངས་ཀས་བརྗིད། རྣམ་གྲོལ་ཐར་བའི་ནོར་བུས་གཙུག་ན་མཛེས། མཆོག་གཉིས་ཀླུ་ཡི་དབང་པོ་ལ་ཕྱག་འཚལ། ཐུབ་པའི་བསྟན་ལ་ཤིང་རྟའི་སྲོལ་བཏོད་ནས། སྐྱེ་རྒུ་རྣམས་ལ་ལེགས་པའི་ལམ་སྟོན་པ། འཛམ་གླིང་མཛེས་བྱེད་རྒྱན་དྲུག་མཆོག་གཉིས་ལ། རྟག་ཏུ་གུས་པའི་ཡིད་ཀྱིས་ཕྱག་འཚལ་ལོ། ";
         static let TSA_SUM_KUN_DU_CONTENT: String = "༈ རྩ་གསུམ་ཀུན་འདུས་སློབ་དཔོན་པདྨ་འབྱུང་། མཁན་ཆེན་ཞི་འཚོ་ཆོས་རྒྱལ་ཁྲི་སྲོང་ཞབས། གནུབས་ཆེན་སངས་རྒྱས་ཉང་སྟོན་ཉི་མ་འོད། བཀའ་གཏེར་ཤིང་རྟ་ལྔ་ལ་གསོལ་བ་འདེབས། རྡོ་རྗེ་འཆང་དངོས་བླ་ཆེན་སྙིང་པོའི་ཞབས། བསོད་ནམས་རྩེ་མོ་གྲཌ་པ་རྒྱལ་མཚན་དང་། ས་སྐྱ་པཎ་ཆེན་འཕགས་པ་རིན་པོ་ཆེ། རྗེ་བཙུན་གོང་མ་ལྔ་ལ་གསོལ་བ་འདེབས། དགྱེས་མཛད་རྡོ་རྗེ་མར་སྟོན་བློ་གྲོས་ཞབས། མི་ལ་རས་ཆེན་མཉམ་མེད་སྒམ་པོ་པ། དུས་གསུམ་མཁྱེན་པ་འགྲོ་མགོན་བསོད་ནམས་གྲགས། བཀའ་བརྒྱུད་གོང་མ་ལྔ་ལ་གསོལ་བ་འདེབས། འོད་དཔག་མེད་མགོན་དཔལ་ལྡན་མར་མེ་མཛད། རྒྱལ་བའི་འབྱུང་གནས་སྤྱན་སྔ་ཚུལ་ཁྲིམས་འབར། རིན་ཆེན་གསལ་དང་གཞོན་ནུ་རྒྱལ་མཚན་ཞབས། བཀའ་གདམས་བཤེས་གཉེན་ལྔ་ལ་གསོལ་བ་འདེབས། བརྟན་པའི་འཁོར་ལོ་རྗེ་བཙུན་ཙོང་ཁ་པ། རྒྱལ་ཚབ་ཆོས་རྗེ་མཁས་གྲུབ་དགེ་ལེགས་དཔལ། དགེ་འདུན་གྲུབ་པ་པཎ་ཆེན་ཆོས་ཀྱི་རྒྱལ། འཇམ་མགོན་ཡབ་སྲས་ལྔ་ལ་གསོལ་བ་འདེབས། དེ་ལྟར་གསོལ་བ་བཏབ་པའི་བྱིན་རླབས་ཀྱིས། བདག་སོགས་ཚེ་རིང་ནད་མེད་ཆོས་བཞིན་སྤྱོད། ཡོངས་འཛིན་བཤེས་གཉེན་མཆོག་གིས་རྗེས་བཟུང་ནས། མྱུར་དུ་བྱང་ཆུབ་གོ་འཕང་ཐོབ་པར་ཤོག རིས་མེད་ཐུབ་བསྟན་འཛིན་པའི་དམ་པ་རྣམས། བསྐལ་བརྒྱར་ཞབས་བརྟན་འཕྲིན་ལས་ཕྱོགས་བཅུར་འཕེལ། དགེ་འདུན་སྡེ་དང་བཤད་སྒྲུབ་བསྟན་པ་རྒྱས། ས་གསུམ་བཀྲ་ཤིས་སྣང་བས་ཁྱབ་གྱུར་ཅིག །།";
         static let GYALWAY_JABTEN_CONTENT: String = "༈ ཨོཾ་སྭ་སྟི། རབ་འབྱམས་རྒྱལ་བའི་གསང་གསུམ་མ་ལུས་པ། གང་འདུལ་ཅི་ཡང་འཆར་བའི་སྒྱུ་འཕྲུལ་གར། སྲིད་ཞིའི་དགེ་ལེགས་ཀུན་འབྱུང་ཡིད་བཞིན་ནོར། དངོས་བརྒྱུད་དྲིན་ཅན་བླ་མའི་ཚོགས་རྣམས་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས། ཆོས་དབྱིངས་ཀུན་གསལ་ཁྱོན་དང་མཉམ་འཇུག་པའི། རྡུལ་བྲལ་བདེ་ཆེན་ཡེ་ཤེས་སྒྱུ་མའི་སྤྲིན། གྲངས་མེད་རྟེན་དང་བརྟེན་པའི་དཀྱིལ་འཁོར་དུ། ཤར་བའི་ཡིད་དམ་ལྷ་ཚོགས་ཐམས་ཅད་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས། སྤངས་རྟོགས་ཡོན་ཏན་ལྷུན་རྫོགས་འཕྲིན་ལས་ཀྱི། སྣང་བ་འགྲོ་ཁམས་རྒྱ་མཚོར་རྟག་རྩེན་པས། ཕན་མཛད་སྟོབས་བཅུ་མངའ་བ་ལྷ་ཡི་ལྷ། རབ་འབྱམས་དུས་གསུམ་རྒྱལ་བ་ཐམས་ཅད་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས། འཇིག་རྟེན་གསུམ་ལས་གང་གིས་ངེས་སྒྲོལ་ཞིང་། མཆོག་ཏུ་ཞི་བ་རྣམ་བྱང་ནོར་བུའི་གཏེར། ཟག་མེད་མི་གཡོ་ཀུན་བཟང་དགེ་བའི་དཔལ། ཐེག་གསུམ་དམ་པའི་ཆོས་ཀྱི་ཚོགས་རྣམས་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས། སྲིད་པའི་འཕྲུལ་འཁོར་འཇོམས་ལ་ཆེས་དཔའ་བའི། བདེན་དོན་མངོན་སུམ་འཇལ་བའི་ཡེ་ཤེས་ཅན།། རྣམ་ཐར་རྡོ་རྗེའི་གྲོང་ལས་མི་ཕྱེད་པ། རིག་གྲོལ་འཕགས་པའི་དགེ་འདུན་ཐམས་ཅད་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས། མཁའ་སྤྱོད་ཞིང་དང་གནས་ཡུལ་དུར་ཁྲོད་དུ། བདེ་སྟོང་ཉམས་བརྒྱར་རོལ་བའི་རྩེད་འཇོ་ཡིས། རྣལ་འབྱོར་ལམ་བཟང་སྒྲུབ་ལ་གྲོགས་ར་ལ མཛད་པའི། གནས་གསུམ་དཔའ་བོ་མཁའ་འགྲོའི་ཚོགས་རྣམས་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུ ན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་ཆོ བས། རྡོ་རྗེ་འཆང་གི་བཀའ་རྟགས་ཕྱག་རྒྱའི་མདུད། མི་འབྲལ་རལ་བའི་ཐོད་དུ་ཉེར་བཀོད་ནས། བསྟན་དང་བསྟན་འཛིན་སྐྱོང་བའི་མཐུ་རྩལ་ཅན། ཡེ་ཤེས་སྤྱན་ལྡན་བསྟན་བསྲུང་རྒྱ་མཚོ་ལ། བདག་ཅག་གདུང་ཤུགས་དྲག་པོས་གསོལ་འདེབས་ན། གངས་ཅན་མགོན་པོ་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། སྐུ་ཚེ་མི་ཤིགས་བསྐལ་བརྒྱར་རབ་བརྟན་ཅིང་། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས། དེ་ལྟར་སླུ་མེད་སྐྱབས་ཀྱི་མཆོག་རྣམས་ལ། ཤུགས་དྲག་སྙིང་ནས་གུས་པས་གསོལ་བཏབ་མཐུས། མི་བཟད་སྙིགས་མའི་ཟུག་ངུས་རབ་མནར་པའི། བདག་སོགས་གངས་ལྗོངས་འགྲོ་བའི་མགོན་གཅིག་པུ། ངག་དབང་བློ་བཟང་བསྟན་འཛིན་རྒྱ་མཚོ་མཆོག གསང་གསུམ་མི་ཤིགས་མི་འགྱུར་མི་ནུབ་པར། གཞོམ་ཞིག་ཡོངས་བྲལ་རྡོ་རྗེ་སྙིང་པོའི་ཁྲིར། བསྐལ་བ་རྒྱ་མཚོར་གཡོ་མེད་རྟག་བརྟན་ཤོག རབ་ར འབྱམས་རྒྱལ་བ་ཀུན་གྱི་མཛད་པའི་འཁུར། སྙིང་སྟོབས་ཕྲག་པར་བཟུང་བའི་རླབས་ཆེན་གྱི། ཕྲིན་ལས་ཀུན་ཕན་ནོར་བུའི་སྙིང་པོ་ཅན། བཞེད་པ་ཇི་བཞིན་ལྷུན་གྱིས་འགྲུབ་གྱུར་ཅིག དེའི་མཐུས་རྫོགས་ལྡན་སྐལ་བཟང་ནམ་མཁའི་སྒོ། ལུས་ཅན་ངལ་བསོའི་དཔྱིད་དུ་རྟག་གྲོལ་ཞིང་། ཐུབ་བསྟན་ཕྱོགས་དུས་ཀུན་ཏུ་རབ་དར་བའི། དགེ་མཚན་སྲིད་ཞིའི་རྩེ་མོར་རྒྱས་གྱུར་ཅིག ཕྱག་ན་པདྨོའི་བྱིན་རླབས་བདུད་རྩིའི་རྒྱུན། བདག་སོགས་སྙིང་གི་ཟུངས་སུ་རྟག་སྨིན་ཅིང་། བཀའ་བཞིན་སྒྲུབ་པའི་མཆོད་པས་རབ་བརྙེས་ནས། ཀུན་བཟང་སྤྱོད་མཆོག་རྒྱ་མཚོ་མཐར་སོན་ཤོག རྨད་བྱུང་སྲས་བཅས་རྒྱལ་བའི་བྱིན་རླབས་དང༌། རྟེན་འབྲེལ་སླུ་བ་མེད་པའི་བདེན་པ་དང་། བདག་གི་ལྷག་བསམ་དག་པའི་མཐུ་སྟོབས་ཀྱིས། སྨོན་པའི་དོན་ཀུན་བདེ་བླག་མྱུར་འགྲུབ་ཤོག །། ";
         static let GANGLO_MA_CONTENT: String = "༈ བླ་མ་དང་མགོན་པོ་འཇམ་དཔལ་དབྱངས་ལ་ཕྱག་འཚལ་ལོ། གང་གི་བློ་གྲོས་སྒྲིབ་གཉིས་སྤྲིན་བྲལ་ཉི་ལྟར་རྣམ་དག་རབ་གསལ་བས། ཇི་སྙེད་དོན་ཀུན་ཇི་བཞིན་གཟིགས་ཕྱིར་ཉིད་ཀྱི་ཐུགས་ཀར་གླེགས་བམ་འཛིན། གང་དག་སྲིད་པའི་བཙོན་རར་མ་རིག་མུན་འཐོམས་སྡུག་བསྔལ་གྱིས་གཟིར་བའི། འགྲོ་ཚོགས་ཀུན་ལ་བུ་གཅིག་ལྟར་བརྩེ་ཡན་ལག་དྲུག་ཅུའི་དབྱངས་ལྡན་གསུང་། འབྲུག་ལྟར་ཆེར་སྒྲོགས་ཉོན་མོངས་གཉིད་སློང་ལས་ཀྱི་ལྕགས་སྒྲོག་འགྲོལ་མཛད་ཅིང༌། མ་རིག་མུན་སེལ་སྡུག་བསྔལ་མྱུ་གུ་ཇི་སྙེད་གཅོད་མཛད་རལ་གྲི་བསྣམས། གདོད་ནས་དག་ཅིང་ས་བཅུའི་མཐར་སོན་ཡོན་ཏན་ལུས་རྫོགས་རྒྱལ་སྲས་ཐུ་བོའི་སྐུ། བཅུ་ཕྲག་བཅུ་དང་བཅུ་གཉིས་རྒྱན་སྤྲས་བདག་བློའི་མུན་སེལ་འཇམ་པའི་དབྱངས་ལ་འདུད། །། \n" +
                 " ཨོཾ་ཨ་ར་པ་ཙ་ན་དྷཱི ། \n" +
                 " བརྩེ་ལྡན་ཁྱེད་ཀྱི་མཁྱེན་རབ་འདོ་ཟེར་གྱིས། བདག་བློའི་གཏི་མུག་མུན་པ་རབ་བསལ་ནས། བཀའ་དང་བསྟན་བཅོས་གཞུང་ལུགས་རྟོགས་པ་ཡི། བློ་གྲོས་སྤོབས་པའི་སྣང་བ་སྩལ་དུ་གསོལ། །།";
         static let NGOWA_MONLAM_ONE_CONTENT: String = "༈ འཇམ་དཔལ་དབའ་བོས་ཇི་ལྟར་མཁྱེན་པ་དང་། ཀུན་ཏུ་བཟང་པོ་དེ་ཡང་དེ་བཞིན་དུ། དེ་དག་ཀུན་གྱི་རྗེས་སུ་བདག་སློབ་ཅིང་། དགེ་བ་འདི་དག་ཐམས་ཅད་རབ་ཏུ་བསྔོ། །།\n" +
                 " ༈ བྱང་ཆུབ་སེམས་མཆོག་རིན་པོ་ཆེ། མ་སྐྱེས་པ་རྣམས་སྐྱེ་གྱུར་ཅིག སྐྱེས་པ་ཉམས་པ་མེད་པ་ཡང་། གོང་ནས་གོང་དུ་འཕེལ་བར་ཤོག ། \n" +
                 "༈ གངས་རི་ར་བས་བསྐོར་བའི་ཞིང་ཁམས་འདིར། ཕན་དང་བདེ་བ་མ་ལུས་འབྱུང་བས་གནས། སྤྱན་རས་གཟིགས་དབང་བསྟན་འཛིན་རྒྱ་མཚོ་ཡི། ཞབས་པད་སྲིད་མཐའི་བར་དུ་བརྟན་གྱུར་ཅིག ། \n" +
                 " ༈ མདོར་ན་དགོན་པོ་སྤྱན་རས་གཟིགས་དབང་གི། སྲས་བཅས་རྒྱལ་བའི་སྤྱན་སྔར་གངས་ཅན་ཞིང༌། ཡོངས་བཟུང་རྒྱ་ཆེན་སྨོན་ལམ་གང་མཛད་པའི། འབྲས་བཟང་དེང་འདིར་མྱུར་དུ་འཆར་བར་གསོལ། །། \n" +
                 " ༈ སྨོན་ལམ་ལོག་པའི་༧རྒྱལ་བའི་བསྟན་པ་ལ། འཁུ་བའི་ནག་ཕྱོགས་གཟུགས་ཅན་གཟུགས་མེད་སྡེ། བསམ་སྦྱོར་ངན་པའི་འཇུག་པ་མ་ལུས་པ། མཆོག་གསུམ་བདེན་པས་རྩད་ནས་གཅོད་གྱུར་ཅིག །།";
         static let DOLMA_CONTENT: String = "༈ པོ་ཏ་ལ་ཡི་གནས་མཆོག་ནས། ཏཾ་ཡིག་ལྗང་ཁུ་ལས་འཁྲུངས་ཤིང་། ཏཾ་ཡིག་འོད་ཀྱིས་འགྲོ་བ་སྒྲོལ། སྒྲོལ་མ་འཁོར་བཅས་གཤེགས་སུ་གསོལ། ལྷ་དང་ལྷ་མིན་ཅོད་པན་གྱི། ཞབས་ཀྱི་པདྨོ་ལ་བཏུད་ནས། ཕོངས་པ་ཀུན་ལས་སྒྲོལ་མཛད་མ། སྒྲོལ་མའི་ཡུམ་ལ་ཕྱག་འཚལ་ལོ། །། ༈ ཨོཾ་རྗེ་བཙུན་མ་འཕགས་མ་སྒྲོལ་མ་ལ་ཕྱག་འཚལ་ལོ། ཕྱག་འཚལ་མ། སྒྲོལ་མ་མྱུར་མ་དཔའ་མོ། སྤྱན་ནི་སྐད་ཅིག་གློག་དང་འདྲ་མ། འཇིག་རྟེན་གསུམ་མགོན་ཆུ་སྐྱེས་ཞལ་གྱི། གེ་སར་བྱེ་བ་ལས་ནི་བྱུང་མ། ཕྱག་འཚལ་སྟོན་ཀའི་ཟླ་བ་ཀུན་ཏུ། གང་བ་བརྒྱ་ནི་བརྩེགས་པའི་ཞལ་མ། སྐར་མ་སྟོང་ཕྲག་ཚོགས་པ་རྣམས་ཀྱིས། རབ་ཏུ་ཕྱེ་བའི་འོད་རབ་འབར་མ། ཕྱག་འཚལ་གསེར་སྔོ་ཆུ་ནས་སྐྱེས་ཀྱི། པདྨས་ཕྱག་ནི་རྣམ་པར་བརྒྱན་མ། སྦྱིན་པ་བརྩོན་འགྲུས་དཀའ་ཐུབ་ཞི་བ། བཟོད་པ་བསམ་གཏན་སྤྱོད་ཡུལ་ཉིད་མ། ཕྱག་འཚལ་དེ་བཞིན་གཤེགས་པའི་གཙུག་ཏོར། མཐའ་ཡས་རྣམ་པར་རྒྱལ་བར་སྤྱོད་མ། མ་ལུས་ཕ་རོལ་ཕྱིན་པ་ཐོབ་པའི།རྒྱལ་བའི་སྲས་ཀྱིས་ཤིན་ཏུ་བསྟེན་མ། ཕྱག་འཚལ་ཏུ་ཏྟཱ་ར་ཧཱུཾ་ཡི་གེས། འདོད་དང་ཕྱོགས་དང་ནམ་མཁའ་གང་མ། འཇིག་རྟེན་བདུན་པོ་ཞབས་ཀྱིས་མནན་ཏེ། ལུས་པ་མེད་པར་འགུགས་པར་ནུས་མ། ཕྱག་འཚལ་བརྒྱ་བྱིན་མེ་ལྷ་ཚངས་པ། རླུང་ལྷ་སྣ་ཚོགས་དབང་ཕྱུག་མཆོད་མ། འབྱུང་པོ་རོ་ལངས་དྲི་ཟ་རྣམས་དང་། གནོད་སྦྱིན་ཚོགས་ཀྱིས་མདུན་ནས་བསྟོད་མ། ཕྱག་འཚལ་ཏྲད་ཅེས་བྱ་དང་ཕཌ་ཀྱིས། ཕ་རོལ་འཕྲུལ་འཁོར་རབ་ཏུ་འཇོམས་མ། གཡས་བསྐུམས་གཡོན་བརྐྱངས་ཞབས་ཀྱིས་མནན་ཏེ། མེ་འབར་འཁྲུགས་པ་ཤིན་ཏུ་འབར་མ། ཕྱག་འཚལ་ཏུ་རེ་འཇིགས་པ་ཆེན་པོ། བདུད་ཀྱི་དཔའ་བོ་རྣམ་པར་འཇོམས་མ། ཆུ་སྐྱེས་ཞལ་ནི་ཁྲོ་གཉེར་ལྡན་མཛད། དགྲ་བོ་ཐམས་ཅད་མ་ལུས་གསོད་མ། ཕྱག་འཚལ་དཀོན་མཆོག་གསུམ་མཚོན་ཕྱག་རྒྱའི། སོར་མོས་ཐུགས་ཀར་རྣམ་པར་བརྒྱན་མ། མ་ལུས་ཕྱོགས་ཀྱི་འཁོར་ལོས་བརྒྱན་པའི། རང་གི་འོད་ཀྱི་ཚོགས་རྣམས་འཁྲུགས་མ། ཕྱག་འཚལ་རབ་ཏུ་དགའ་བ་བརྗིད་པའི། དབུ་རྒྱན་འོད་ཀྱི་ཕྲེང་བ་སྤེལ་མ། བཞད་པ་རབ་བཞད་ཏུ་ཏྟཱ་ར་ཡིས། བདུད་དང་འཇིག་རྟེན་དབང་དུ་མཛད་མ། ཕྱག་འཚལ་ས་གཞི་སྐྱོང་བའི་ཚོགས་རྣམས། ཐམས་ཅད་འགུགས་པར་ནུས་པ་ཉིད་མ། ཁྲོ་གཉེར་གཡོ་བའི་ཡི་གེ་ཧཱུྃ་གིས། ཕོངས་པ་ཐམས་ཅད་རྣམ་པར་སྒྲོལ་མ། ཕྱག་འཚལ་ཟླ་བའི་དུམ་བུས་དབུ་རྒྱན། བརྒྱན་པ་ཐམས་ཅད་ཤིན་ཏུ་འབར་མ། རལ་པའི་ཁྲོད་ན་འོད་དཔག་མེད་ལས། རྟག་པར་ཤིན་ཏུ་འོད་རབ་མཛད་མ། ཕྱག་འཚལ་བསྐལ་པའི་ཐ་མའི་མེ་ལྟར། འབར་བའི་ཕྲེང་བའི་དབུས་ན་གནས་མ། གཡས་བསྐྱངས་གཡོན་བསྐུམས་ཀུན་ནས་བསྐོར་དགའི། དགྲ་ཡི་དཔུང་ནི་རྣམ་པར་འཇོམས་མ། ཕྱག་འཚལ་ས་གཞིའི་ངོས་ལ་ཕྱག་གི། མཐིལ་གྱིས་བསྣུན་ཅིང་ཞབས་ཀྱིས་བརྡུང་མ། ཁྲོ་གཉེར་སྤྱན་མཛད་ཡི་གེ་ཧཱུཾ་གིས། རིམ་པ་བདུན་པོ་རྣམས་ནི་ཡི་གེ འགེམས་མ། ཕྱག་འཚལ་བདེ་མ་དགེ་མ་ཞི་མ། མྱ་ངན་འདས་ཞི་སྤྱོད་ཡུལ་ཉིད་མ། སྭ་ཧཱ་ཨོཾ་དང་ཡང་དག་ལྡན་པས། སྡིག་པ་ཆེན་པོ་འཇོམས་པ་ཉིད་མ། ཕྱག་འཚལ་ཀུན་ནས་བསྐོར་རབ་དགའ་བའི། དགྲ་ཡི་ལུས་ནི་རྣམ་པར་འགེམས་མ། ཡི་གེ་བཅུ་པའི་ངག་ནི་བཀོད་པའི། རིག་པ་ཧཱུྃ་ལས་སྒྲོལ་མ་ཉིད་མ། ཕྱག་འཚལ་ཏུ་རེ་ཞབས་ནི་བརྡབས་པས། ཧཱུྃ་གི་རྣམ་པའི་ས་བོན་ཉིད་མ། རི་རབ་མཎྜ་ར་དང་འབིགས་བྱེད། འཇིག་རྟེན་གསུམ་རྣམས་གཡོ་བ་ཉིད་མ། ཕྱག་འཚལ་ལྷ་ཡི་མཚོ་ཡི་རྣམ་པའི། རི་དྭགས་རྟགས་ཅན་ཕྱག་ན་བསྣམས་མ། ཏཱ་ར་གཉིས་བརྗོད་ཕཌ་ཀྱི་ཡི་གེས། དུགས་རྣམས་མ་ལུས་པ་ནི་སེལ་མ། ཕྱག་འཚལ་ལྷ་ཡི་ཚོགས་རྣམས་རྒྱལ་པོ། ལྷ་དང་མིའམ་ཅི་ཡིས་བསྟེན་མ། ཀུན་ནས་གོ་ཆ་དགའ་བའི་བརྗིད་ཀྱིས། རྩོད་དང་རྨི་ལམ་ངན་པ་སེལ་མ། ཕྱག་འཚལ་ཉི་མ་ཟླ་བ་རྒྱས་པའི། སྤྱན་གཉིས་པོ་ལ་འོད་རབ་གསལ་མ། ཧ་ར་གཉིས་བརྗོད་ཏུཏྟཱ་ར་ཡིས། ཤིན་ཏུ་དྲག་པོའི་རིམས་ནད་སེལ་མ། ཕྱག་འཚལ་དེ་ཉིད་གསུམ་རྣམས་བཀོད་པས། ཞི་བའི་མཐུ་དང་ཡང་དག་ལྡན་མ། གདོན་དང་རོ་ལངས་གནོད་སྦྱིན་ཚོགས་རྣམས། འཇོམས་པ་ཏུ་རེ་རབ་མཆོག་ཉིད་མ། རྩ་བའི་སྔགས་ཀྱི་བསྟོད་པ་འདི་དང་། ཕྱག་འཚལ་བ་ནི་ཉི་ཤུ་རྩ་གཅིག །། \n" +
                 " ༈ རྗེ་བཙུན་འཕགས་མ་སྒྲོལ་མ་དང་། ཕྱོགས་བཅུ་དུས་གསུམ་བཞུགས་པ་ཡི། རྒྱལ་བ་སྲས་བཅས་ཐམས་ཅད་ལ། ཀུན་ནས་དང་བས་ཕྱག་འཚལ་བགྱིའོ། མེ་ཏོག་བདུག་སྤོས་མར་མེ་དྲི། ཞལ་ཟས་རོལ་མོ་ལ་སོགས་ས་པ། དངོས་བཤམས་ཡིད་ཀྱིས་སྤྲུལ་ནས་འབུལ། འཕགས་པའི་ཚོགས་ཀྱིས་བཞེས་སུ་གསོལ། ཐོག་མ་མེད་ནས་ད་ལྟའི་བར། མི་དགེ་བཅུ་དང་མཚམས་མེད་ལྔ། སེམས་ནི་ཉོན་མོངས་དབང་གྱུར་པས། བགྱིས་པ་ཐམས་ཅད་བཤགས་པར་བགྱི། ཉན་ཐོས་རང་རྒྱལ་བྱང་ཆུབ་སེམས། སོ་སོ་སྐྱེ་བོ་ལ་སོགས་པས། དུས་གསུམ་དགེ་བ་ཅི་བསགས་པའི། བསོད་ནམས་ལ་ནི་རྗེས་ཡི་རང་། སེམས་ཅན་རྣམས་ཀྱི་བསམ་པ་དང་། བློ་ཡི་བྱེ་བྲག་ཇི་ལྟ་བར། ཆེ་ཆུང་ཐུན་མོང་ཐེག་པ་ཡི། ཆོས་ཀྱི་འཁོར་ལོ་བསྐོར་དུ་གསོལ། འཁོར་བ་ཇི་སྲིད་མ་སྟོངས་བར། མྱ་ངན་མི་འདའ་ཐུགས་རྗེ་ཡིས། སྡུག་བསྔལ་རྒྱ་མཚོར་བྱིང་བ་ཡི། སེམས་ཅན་རྣམས་ལ་གཟིགས་སུ་གསོལ། བདག་གིས་བསོད་ནམས་ཅི་བསགས་པ། ཐམས་ཅད་བྱང་ཆུབ་རྒྱུར་གྱུར་ནས། རིང་པོར་མི་ཐོགས་འགྲོ་བ་ཡི། འདྲེན་པའི་དཔལ་དུ་བདག་གྱུར་ཅིག ། ༈ རྗེ་བཙུན་སྒྲོལ་མ་ཁྱོད་ཀྱིས་བདག་དང་། ནམ་མཁའི་མཐའ་དང་མཉམ་པའི་སེམས་ཅན་ཐམས་ཅད་ཀྱི་ཚེ་དང་བསོད་ནམས་འཕེལ་ཞིང་རྒྱས་པར་མཛད་དུ་གསོལ། བདག་གི་ཚེ་བསྲིངས་བར་མཛད་དུ་གསོལ། ནད་དང་གདོན་ཞི་བར་མཛད་དུ་གསོལ། སྡིག་སྒྲིབ་ཐམས་ཅད་བྱང་མཛད་དུ་གསོལ། མཆོག་དང་ཐུན་མོང་གི་དངོས་གྲུབ་མྱུར་དུ་ཐོབ་བར་མཛད་པར་མཛད་དུ་གསོལ། མདོ་དང་རྒྱུད་སྡེ་རྒྱ་མཚོའི་དམ་ཆོས་ཀུན། ལེཌ་པར་རྟོ ཌ་ནས་གཞན་ལ་འདོམས་པ་དང་། ཚུལ་བཞིན་སྒྲུབ་པའི་རྒྱལ་མཚན་འཛིན་པ་ལ། འཁོར་བ་མ་སྟོང་བར་དུ་བརྩོན་པར་ཤོག །། \n" +
                 " ༈ ཁྱོད་ལ་བསྟོད་ཅིང་གསོལ་བ་བཏབ་པའི་མཐུས། བདག་སོགས་གང་ན་གནས་པའི་ས་ཕྱོགས་དེར། ནད་གདོན་དབུལ་ཕོངས་འཐབ་རྩོད་ཞི་བ་དང་། ཆོས་དང་བཀྲ་ཤིས་འཕེལ་བར་མཛད་དུ་གསོལ། །།";
         static let SHERAB_NYINGPO_CONTENT: String = "༈ འཕགས་པ་དཀོན་མཆོག་གསུམ་ལ་ཕྱག་འཚལ་ལོ། འདི་སྐད་བདག་གི་ཐོས་པ་དུས་གཅིག་ན། བཅོམ་ལྡན་འདས་རྒྱལ་པོའི་ཁབ་བྱ་རྒོད་ཕུང་པོའི་རི་ལ་དགེ་སློང་གི་དགེ་འདུན་ཆེན་པོ་དང་བྱང་ཆུབ་སེམས་དཔའི་དགེ་འདུན་ཆེན་པོ་དང་ཐབས་གཅིག་ཏུ་བཞུགས་ཏེ། དེའི་ཚེ་བཅོམ་ལྡན་འདས་ཟབ་མོ་སྣང་བ་ཞེས་བྱ་བའི་ཆོས་ཀྱི་རྣམ་གྲངས་ཀྱི་ཏིང་ངེ་འཛིན་ལ་སྙོམས་པར་ཞུགས་སོ། ཡང་དེའི་ཚེ་བྱང་ཆུབ་སེམས་དཔའ་སེམས་དཔའ་ཆེན་པོ་འཕགས་པ་སྤྱན་རས་གཟིགས་དབང་ཕྱུག་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པ་ཟབ་མོའི་སྤྱོད་པ་ཉིད་ལ་རྣམ་པར་བལྟ་ཞིང་ཕུང་པོ་ལྔ་པོ་དེ་དག་ལ་ཡང་རང་བཞིན་གྱིས་སྟོང་པ་རྣམ་པར་བལྟའོ། དེ་ནས་སངས་རྒྱས་ཀྱི་མཐུས་ཚེ་དང་ལྡན་པ་ཤཱ་རིའི་བུས་བྱང་ཆུབ་སེམས་དཔའ་སེམས་དཔའ་ཆེན་པོ་འཕགས་པ་སྤྱན་རས་གཟིགས་དབང་ཕྱུག་ལ་འདི་སྐད་ཅེས་སྨྲས་སོ། རིགས་ཀྱི་བུ། གང་ལ་ལ་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པ་ཟབ་མོའི་སྤྱོད་པ་སྤྱད་པར་འདོད་པ་དེས་ཇི་ལྟར་བསླབ་བར་བྱ། དེ་སྐད་ཅེས་སྨྲས་པ་དང་། བྱང་ཆུབ་སེམས་དཔའ་སེམས་དཔའ་ཆེན་པོ་འཕགས་པ་སྤྱན་རས་གཟིགས་དབང་ཕྱུག་གིས་ཚེ་དང་ལྡན་པ་ཤཱ་རི་དྭ་ཏིའི་བུ་ལ་འདི་སྐད་ཅེས་སྨྲས་སོ། ཤཱ་རིའི་བུ། རིགས་ཀྱི་བུའམ་རིགས་ཀྱི་བུ་མོ་གང་ལ་ལ་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པ་ཟབ་མོའི་སྤྱད་པ་སྤྱོད་པར་འདོད་པ་དེས་འདི་ལྟར་རྣམ་པར་བལྟ་བར་བྱ་སྟེ། ཕུང་པོ་ལྔ་པོ་དེ་དག་ཀྱང་རང་བཞིན་གྱིས་སྟོང་པར་རྣམ་པར་ཡང་དག་པར་རྗེས་སུ་བལྟའོ། གཟུགས་སྟོང་པའོ། སྟོང་པ་ཉིད་གཟུགས་སོ། གཟུགས་ལས་ཀྱང་སྟོང་པ་ཉིད་གཞན་མ་ཡིན། སྟོང་པ་ཉིད་ལས་ཀྱང་གཟུགས་གཞན་མ་ཡིན་ནོ། དེ་བཞིན་དུ་ཚོར་བ་དང་། འདུ་ཤེས་དང་། འདུ་བྱེད་དང༌། རྣམ་པར་ཤེས་པ་རྣམས་སྟོང་པའོ། ཤཱ་རིའི་བུ། དེ་ལྟར་ཆོས་ཐམས་ཅད་སྟོང་པ་ཉིད་དེ། མཚན་ཉིད་མེད་པ། མ་སྐྱེས་པ། མ་འགགས་པ། དྲི་མ་མེད་པ། དྲི་མ་དང་བྲལ་བ། བྲི་བ་མེད་པ། གང་བ་མེད་པའོ། ཤཱ་རིའི་བུ། དེ་ལྟ་བས་ན། སྟོང་པ་ཉིད་ལ་གཟུགས་མེད། ཚོར་བ་མེད། འདུ་ཤེས་མེད། འདུ་བྱེད་རྣམས་མེད། རྣམ་པར་ཤེས་པ་མེད། མིག་མེད། རྣ་བ་མེད། སྣ་མེད། ལྕེ་མེད། ལུས་མེད། ཡིད་མེད། གཟུགས་མེད། སྒྲ་མེད། དྲི་མེད། རོ་མེད། རེག་བྱ་མེད། ཆོས་མེད་དོ། མིག་གི་ཁམས་མེད་པ་ནས་ཡིད་ཀྱི་ཁམས་མེད། ཡིད་ཀྱི་རྣམ་པར་ཤེས་པའི་ཁམས་ཀྱི་བར་དུ་ཡང་མེད་དོ། མ་རིག་པ་མེད། མ་རིག་པ་ཟད་པ་མེད་པ་ནས་རྒ་ཤི་མེད། རྒ་ཤི་ཟད་པའི་བར་དུའང་མེད་དོ། དེ་བཞིན་དུ་སྡུག་བསྔལ་བ་དང་། ཀུན་འབྱུང་བ་དང་། འགོག་པ་དང༌། ལམ་མེད། ཡེ་ཤེས་མེད། ཐོབ་པ་མེད། མ་ཐོབ་པ་ཡང་མེད་དོ། ཤཱ་རིའི་བུ། དེ་ལྟ་བས་ན་བྱང་ཆུབ་སེམས་དཔའ་རྣམས་ཐོབ་པ་མེད་པའི་ཕྱིར་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པ་ལ་བརྟེན་ཅིང་གནས་ཏེ། སེམས་ལ་སྒྲིབ་པ་མེད་པས་སྐྲག་པ་མེད་དོ། ཕྱིན་ཅི་ལོག་ལས་ཤིན་ཏུ་འདས་ནས་མྱ་ངན་ལས་འདས་པའི་མཐར་ཕྱིན་ཏོ། དུས་གསུམ་དུ་རྣམ་པར་བཞུགས་པའི་སངས་རྒྱས་ཐམས་ཅད་ཀྱང་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པ་ལ་བརྟེན་ནས་བླ་ན་མེད་པ་ཡང་དག་པར་རྫོགས་པའི་བྱང་ཆུབ་ཏུ་མངོན་པར་རྫོགས་པར་སངས་རྒྱས་སོ། དེ་ལྟ་བས་ན་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པའི་སྔགས། རིག་པ་ཆེན་པོའི་སྔགས། བླ་ན་མེད་པའི་སྔགས། མི་མཉམ་པ་དང་མཉམ་པའི་སྔགས། སྡུག་བསྔལ་ཐམས་ཅད་རབ་ཏུ་ཞི་བར་བྱེད་པའི་སྔགས། མི་རྫུན་བས་ན་བདེན་པར་ཤེས་པར་བྱ་སྟེ། ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པའི་སྔགས་སྨྲས་པ། ཏདྱཐཱ། ག་ཏེ་ག་ཏེ་པཱ་ར་ག་ཏེ་པཱ་ར་སཾ་ག་ཏེ་བོ་དྷི་སྭཱཧཱ། ཤཱ་རིའི་བུ། བྱང་ཆུབ་སེམས་དཔའ་སེམས་དཔའ་ཆེན་པོས་དེ་ལྟར་ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པའི་ཟབ་མོ་ལ་བསླབ་པར་བྱའོ། དེ་ནས་བཅོམ་ལྡན་འདས་ཏིང་ངེ་འཛིན་དེ་ལས་བཞེངས་ཏེ། བྱང་ཆུབ་སེམས་དཔའ་སེམས་དཔའ་ཆེན་པོ་འཕགས་པ་སྤྱན་རས་གཟིགས་དབང་ཕྱུག་ལ་ལེགས་སོ། ཞེས་བྱ་བ་བྱིན་ནས། ལེགས་སོ་ལེགས་སོ། རིགས་ཀྱི་བུ། དེ་དེ་བཞིན་ནོ། རིགས་ཀྱི་བུ་དེ་དེ་བཞིན་ཏེ། ཇི་ལྟར་ཁྱོད་ཀྱིས་བསྟན་པ་བཞིན་དུ། ཤེས་རབ་ཀྱི་ཕ་རོལ་ཏུ་ཕྱིན་པ་ཟབ་མོ་ལ་སྤྱད་པར་བྱ་སྟེ། དེ་བཞིན་གཤེགས་པ་རྣམས་ཀྱང་རྗེས་སུ་ཡི་རང་ངོ་། བཅོམ་ལྡན་འདས་ཀྱིས་དེ་སྐད་ཅེས་བཀའ་བསྩལ་ནས། ཚེ་དང་ལྡན་པ་ཤཱ་རི་དྭ་ཏིའི་བུ་དང༌། བྱང་ཆུབ་སེམས་དཔའ་སེམས་དཔའ་ཆེན་པོ་འཕགས་པ་སྤྱན་རས་གཟིགས་དབང་ཕྱུག་དང་། ཐམས་ཅད་དང་ལྡན་པའི་འཁོར་དེ་དག་དང་། ལྷ་དང༌། མི་དང༌། ལྷ་མ་ཡིན་དང་། དྲི་ཟར་བཅས་པའི་འཇིག་རྟེན་ཡི་རངས་ཏེ། བཅོམ་ལྡན་འདས་ཀྱིས་གསུངས་པ་ལ་མངོན་པར་བསྟོད་དོ། ཏདྱཐཱ། ག་ཏེ་ག་ཏེ་པཱ་ར་ག་ཏེ་པ་ར་སཾ་ག་ཏེ་བོ་དྷི་སྭཱཧཱ། འཕགས་པ་དཀོན་མཆོག་གསུམ་གྱི་བཀའ་ཡི་བདེན་པ་ཆེན་པོའི་སྟོབས་ཀྱིས་མི་མཐུན་པའི་ཕྱོགས་ཐམས་ཅད་ཕྱིར་བཟློག་པར་གྱུར་ཅིག་ མེད་པར་གྱུར་ཅིག་ ཞི་བར་གྱུར་ཅིག་ བགེགས་རིགས་སྟོང་ཕྲག་བརྒྱད་ཅུ་ཞི་བ་དང་། མི་མཐུན་གནོད་པའི་རྐྱེན་དང་བྲལ་བ་དང་། མཐུན་པར་འགྲུབ་ཅིང་ཕུན་སུམ་ཚོགས་གྱུར་པའི། བཀྲ་ཤིས་དེས་ཀྱང་དེང་འདིར་བདེ་ལེགསཤོག །། ";
         static let LHAMO_YANLAK_CONTENT: String = "༈ བྷོ དཔལ་ལྡན་ལྷ་མོ་དཔག་པར་དཀའ་བ་ཁྱོད། ཐབས་ཀྱི་ཆོ་འཕྲུལ་ནམ་མཁའི་མཐའ་ལ་སྤྱོད། བྱང་ཆུབ་སེམས་དཔའི་སྤྲུལ་པ་ཁྱོད་ཉིད་ལ། ལུས་ངག་ཡིད་གསུམ་གུས་པས་ཕྱག་འཚལ་ལོ། ཁྱོད་ནི་བདག་གི་མཆོད་པས་མ་བསྒྲིབས་ཀྱང་། དཔལ་ལྡན་དངོས་གྲུབ་དམ་པ་ཐོབ་བྱའི་ཕྱིར། འདོད་པའི་ཡོན་ཏན་མཁའ་ལ་ཆར་ཕབ་ནས། མཆོད་པའི་ཚོགས་འདིས་དབང་པོ་རྒྱས་གྱུར་ཅིག དཔལ་ལྡན་ཁྱོད་ནི་དགེ་སྡིག་དཔང་པོ་སྟེ། བདག་གི་ལུས་ངག་ཡིད་ཀྱི་ཉེས་པའི་ཚོགས། ཁྱོད་ཀྱི་ཐད་དུ་འགྱོད་པས་རབ་བཤགས་ན། ཐུགས་རྗེའི་ཤུགས་ཀྱིས་སྒྲིབ་པ་བྱང་བར་མཛོད། ཁྱོད་ཀྱི་ཁྱད་པར་ཡོངས་སུ་འཕགས་པ་ནི། འགྲོ་བའི་དོན་མཛད་བདག་དོན་ཕུན་སུམ་ཚོགས། དགའ་མཐུན་ཕྲག་དོག་མེད་པའི་སྤྲོ་བ་ནི། རྗེས་སུ་ཡི་རང་ཁྱོད་ཀྱི་རྒྱུད་ལ་འབུལ། ཡང་དག་ཡོན་རང ཏན་ལྡན་པའི་སྐུ་མཆོག་ནི། ནག་དང་བཟང་མོ་འཇིགས་པའི་ཆ་བྱད་ཅན། ཕྱོགས་བཅུ་དུས་གསུམ་ཀུན་ཏུ་འགྲོ་དོན་ལ། སྒྱུ་འཕྲུལ་དྲ་བའི་ཚུལ་སྟོན་བསྟན་དུ་གསོལ། དེ་ལྟར་ཡང་དག་ལྡན་པའི་སྐུ་མཆོག་ནི། འགྲོ་བའི་དོན་དང་བསྟན་པ་བསྲུང་བྱའི་ཕྱིར། ནམ་མཁའ་ཟད་པར་མ་གྱུར་བར་དུ་ཡང་། མྱ་ངན་མི་འདའ་རྟག་པར་བཞུགས་སུ་གསོལ། དེ་ལྟར་ཕྱག་འཚལ་མཆོད་ཅིང་བཤགས་པ་དང་། རྗེས་སུ་ཡི་རང་བསྐུལ་ཞིང་གསོལ་བ་ཡི། དགེ་བ་འདི་ཡིས་ངན་སོང་སྟོངས་གྱུར་ནས། ཐམས་ཅད་བདག་གི་བྱང་ཆུབ་ཕྱིར་བསྔོའོ། ། ";
         static let NAYCHUNG_THINLAY_CONTENT: String = "༈ བདེ་གཤེགས་རིགས་ལྔའི་ཡེ་ཤེས་སྒྱུ་འཕྲུལ་གར། བོད་ཁམས་སྐྱོང་མཛད་བསྟན་སྲུང་དྲེགས་པའི་གཙོ། ཕྲིན་ལས་རྒྱལ་པོ་སྤྲུལ་ཡུམ་བློན་པོར་བཅས། བདག་ཅག་གདུང་བའི་དབྱངས་ཀྱིས་ཉེར་བསྐུལ་ན། སྔོན་ཚེ་རྒྱལ་དབང་པདྨ་ཀ་ར་དང་། པད་དཀར་འཆང་བའི་རིམ་པས་བཀའ་བསྒོས་ཤིང་། འདའ་དཀའི་དམ་ཚིག་གཉན་པོ་རབ་བསྒྲགས་པ། མ་གཡེལ་མ་གཡེལ་རྡོ་རྗེའི་དམ་ཚིག་དགོངས། བདག་ཅག་རྨོངས་དང་ཆགས་སྡང་ཕྱོགས་ལྷུང་གིས། ལས་འབྲས་མ་བསྲུངས་སྤྱི་དོན་རས་སུ་བོར། དམ་ཚིག་འགལ་སོགས་བསམ་སྦྱོར་ནོངས་པ་ཀུན། སྙིང་ནས་འཆགས་ཤིང་སྡོམ་ན་བཟོད་མཛོད་ལ། བསྟན་དང་བསྟན་འཛིན་སྐྱེ་འགྲོའི་བདེ་སྐྱིད་དང་། བསྟན་འཛིན་རྒྱ་མཚོའི་སྐུ་ཚེ་མཛད་འཕྲིན་སྤེལ། གངས་ལྗོངས་རྒུད་པའི་ཉེར་འཚེ་མྱུར་ཞི་ནས། ཆོས་སྲིད་རང་དབང་དགའ་སྟོན་སྒྲུབས་ཤིག་ཀྱེ། མདོར་ན་བསྟན་སྲུང་མཐུ་བོ་ཁྱོད་ཉིད་ལ། སྙིང་ནས་བསྐུལ་ཞིང་འདོད་དོན་གཉེར་གཏོད་ན། གློག་ལྟར་མྱུར་བའི་རྣམ་བཞིའི་འཕྲིན་ལས་ཀྱིས། བསམ་པའི་རེ་བ་སྒྲུབ་ལ་མ་གཡེལ་ཞིག །ཅེས་པ་འདི་ནི་ྋརྒྱལ་དབང་སྐུ་ཕྲེང་བཅུ་བཞི་པ་ཆེན་པོས་མཛད་པའོ། །།";
         static let BOD_CHOT_YAYUNG_CONTENT: String = "༈ ཀྱི། མ་སྨད་བསོད་ནམས་མཐུ་དང་སྨོན་ལམ་གྱིས། ཕྱག་ན་པདྨོའི་གདུལ་ཞིང་བསིལ་ལྡན་ལྗོངས། མཐའ་དབུས་ཡུལ་ཁམས་སྐྱོང་ཕྱིར་དེ་དང་དེར། གནས་ཤིང་རྒྱུ་བའི་ཡུལ་ལྷ་གཞི་བདག་དང་། ཁྱད་པར་ཚེ་རིང་མཆེད་ལྔ་བསྟན་མའི་ཚོགས། སྲིད་པའི་ལྷ་དགུ་རྗེ་ཡི་མགུར་ལྷ་སོགས། བོད་སྐྱོང་ལྷ་སྲུང་གཉན་པོ་མཆེད་འཁོར་རྣམས། གདུང་བས་འབོད་དོ་འདིར་བྱོན་མགྲོན་དུ་བཞུགས། རྫས་སྔགས་ཏིང་འཛིན་རོལ་པ་ལས་བྱུང་བའི། དཀར་གསུམ་མངར་གསུམ་ཕྱེ་མར་ཆང་ཕུད་སོགས། ཡིད་འོང་བཟའ་བཅའི་བྱེ་བྲག་དུ་མ་དང་། རིན་ཆེན་དར་ཟབ་གོ་མཚོན་སྤྱན་གཟིགས་ཚོགས། མི་གཙང་ཉེས་ཀུན་སེལ་བའི་བསངས་མཆོད་དང་། སྙིང་ནས་མཛའ་ཞིང་དབུ་འཕང་མཐོར་བཏེག་སྟེ། གསོལ་ལོ་མཆོད་དོ་དམ་སྐོང་དགྱེས་མཛོད་ལ། བག་མེད་ལེ་ལོས་ང ནོངས་པ་ཅི་མཆིས་བཤགས། ཡང་དག་ལྟ་དང་གཞན་ཕན་བྱང་ཆུབ་སེམས། ཐུབ་པའི་བཀའ་ལུང་བསླུ་བ་མེད་པའི་མཐུས། སྙིང་རྗེའི་ཡུལ་གྱུར་ནག་ཕྱོགས་སྡེར་གཏོགས་ཀུན། སྡང་དང་གདུག་པའི་བློ་ངན་རབ་ཞི་ནས། བླང་དོར་འབྱེད་པའི་བློ་མིག་མྱུར་ཐོབ་ཅིང་། དུས་ཀྱི་རྒུད་པའི་མུན་པ་ཡོངས་སངས་ཏེ། ཆོས་སྲིད་རང་དབང་གཙང་མའི་སྣང་བའི་དཔལ། འཆར་བར་ངེས་སོ་དམ་ལྡན་སྙིང་མ་ཞུམ། སྔོན་ཚེ་སློབ་དཔོན་མཚོ་སྐྱེས་རྡོ་རྗེ་དང་། ༧རྒྱལ་བློན་སེམས་དཔའ་དུ་མས་བཀའ་བསྒོས་ཤིང་། ད་ལྟ་རིག་འཛིན་རྣལ་འབྱོར་བདག་ཅག་གིས། གདུང་ཤུགས་དྲག་པོས་མཆོད་ཅིང་ཉེར་བསྐུལ་ན། རང་རང་མཐུ་སྟོབས་ནུས་པའི་དཔུང་བསྐྱེད་དེ། རང་ཡུལ་གངས་ཅན་ཞིང་དུ་བདེ་སྐྱིད་དཔལ། མྱུར་དུ་བཞད་པའི་རྣམ་བཞིའི་འཕྲིན་ལས་ཀུན། མ་ཐོགས་ད་ལྟ་ཉིད་དུ་སྒྲུབས་ཤིག་ཀྱེ། ལྷག་པར་གངས་ཅན་མགོན་པོ་དང་། བསྟན་འཛིན་དམ་པའི་སྐུ་ཚེ་སྲིངས། མཁའ་མཉམ་བསྟན་འགྲོའི་བདེ་སྐྱེད་སྤེལ། བཞེད་དོན་ལྷུན་གྱིས་འགྲུབ་པར་མཛོད། །ཅེས་པ་འདི་ནི་༧གོང་ས་སྐུ་ཕྲེང་བཅུ་བཞི་པ་ཆེན་པོའི་མཛད་པའོ། ། ";
         static let THUPTEN_RINMAT_CONTENT: String = "༈ སྐུ་བཞིའི་བདག་ཉིད་ཀུན་མཁྱེན་ཉི་མའི་གཉེན། ཚེ་འོད་དཔག་མེད་འཕགས་མཆོག་སྤྱན་རས་གཟིགས། འཇམ་དབྱངས་གསང་བདག་སྒྲོལ་མ་ཁྲོ་གཉེར་ཅན། རྒྱལ་དང་རྒྱལ་སྲས་སེམས་དཔའི་ཚོགས་རྣམས་དང་། གཏད་རབས་ཆེ་བདུན་རྒྱན་དྲུག་མཆོག་གཉིས་པོ། གྲུབ་ཆེན་བརྒྱད་ཅུ་གནས་བརྟན་བཅུ་དྲུག་སོགས། བསྟན་དང་འགྲོ་ལ་གཅིག་ཏུ་ཕན་བཞེད་པ། སྐྱེས་མཆོག་སེམས་དཔའ་མ་ལུས་དགོངས་སུ་གསོལ། ཐུབ་པ་མཆོག་གིས་གྲངས་མེད་བསྐལ་བ་རུ། བསོད་ནམས་ཡེ་ཤེས་ཚོགས་གཉིས་རབ་རྫོགས་ཏེ། མཁྱེན་བརྩེའི་ནུས་པ་མཐར་སོན་བདེན་པའི་མཐུས། ཡོངས་རྫོགས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག གངས་རིའི་ལྗོངས་སུ་ཐུབ་བསྟན་སྣང་བའི་སྒོ། ཐོག་མར་འབྱེད་མཛད་མཁན་སློབ་ཆོས་རྒྱལ་གསུམ། ལོ་པཎ་རིག་འཛིན་རྗེ་འབངས་ཐུགས་བསྐྱེད་མཐུས། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག རྒྱ་ཆེན་བཀའ་ཡི་ནོར་བུའི་ལྡིང་ཁང་དུ། ཟབ་མོའི་སྒྲུབ་སྡེ་ཆོས་ཀྱི་གཏེར་ཆེན་པོ། ཟབ་ཡངས་སྙིང་ཏིག་འོད་རྣོན་རབ་ཏུ་འཚེར། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག ཀ་དག་འོད་གསལ་སྙིང་པོའི་ཀློང་ཆེན་པོར། འཁོར་འདས་ཆོས་ཀུན་འུབ་ཆུབ་ཐེག་པའི་རྩེ། ཀུན་བཟང་གདོད་མའི་རྒྱལ་སར་བགྲོད་པའི་ཐབས། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག ཟབ་མོའི་ལྟ་དང་རྒྱ་ཆེན་སྤྱོད་པའི་བརྒྱུད། ཡོངས་རྫོགས་གདམས་པའི་མཛོད་མངའ་ཨ་ཏི་ཤ། རྒྱལ་བའི་འབྱུང་གནས་ལས་འོང་གདམས་པའི་སྲོལ། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག སྡེ་སྣོད་གསུམ་གྱིས་བསྡུས་པའི་རྒྱལ་བའི་བཀའ། གདམས་ས་པ་སྐྱེས་བུ་གསུམ་དུ་ལེགས་བསྒྲིགས་པ། ལྷ་ཆོས་བདུན་ལྡན་བཀའ་ས་བ གདམས་གསེར་གྱི་ཕྲེང་། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་ར གྱུར་ཅིག སྒྲ་སྒྱུར་མར་པ་བཞད་པ་རྡོ་རྗེ་སོགས། བྱིན་རླབས་འབྱུང་གནས་བཀའ་བརྒྱུད་ནོར་བུའི་མཛོད། མཚུངས་མེད་རིམ་བྱོན་བཀའ་སྲོལ་རྨད་བྱུང་བ། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག འཁོར་འདས་ཆོས་ཀུན་ལྷན་སྐྱེས་སེམས་ཀྱི་མདངས། སེམས་ཉིད་སྤྲོས་བྲལ་ཆོས་སྐུའི་ངོ་བོར་རྟོགས། སྣང་སྲིད་འཁོར་འདས་ཀུན་ཁྱབ་ཕྱག་རྒྱ་ཆེ། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག ཕྱི་ནང་རིག་གནས་མདོ་སྔགས་གཞུང་བརྒྱའི་གནད། འཆད་རྩོད་རྩོམ་པས་ཐུབ་བསྟན་སྐྱོང་མཁས་པ། ལྷ་རིགས་འཁོན་སྟོན་བརྩེ་ཆེན་ས་སྐྱ་པ། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག གསུང་ངག་སྙན་བརྒྱུད་ཚད་མ་བཞི་ལྡན་གྱི། རྒྱུ་ལམ་འབྲས་བུའི་ཉམས་ལེན་ཆེས་ཟབ་གནད། རྣལ་འབྱོར་དབང་ཕྱུག་ཁྱད་ཆོས་གདམས་པའི་སྲོལ། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག ཟབ་མོ་དབུ་མའི་ལྟ་དང་ཉེར་འབྲེལ་བའི། གསང་ཆེན་རྡོ་རྗེ་ཐེག་པའི་རིམ་གཉིས་ལམ། ཟབ་གསལ་སྤེལ་མཁས་བློ་བཟང་རྒྱལ་བའི་བསྟན། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག སྡེ་སྣོད་གསུམ་དང་རྒྱུད་སྡེ་བཞི་ཡི་དོན། ཡོངས་རྫོགས་ལམ་གྱི་ངོ་བོར་གོ་རིམ་རྣམས། མ་ནོར་ཉམས་སུ་ལེན་པའི་ལུགས་བཟང་མཆོག གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག མདོ་རྒྱུད་གཞན་དང་བཤད་སྲོལ་མི་མཚུངས་པའི། ཕྱི་ནང་གཞན་གསུམ་དུས་ཀྱི་འཁོར་ལོ་ཡི། ལུང་རྟོགས་བཀའ་བབས་བུ་ཇོ་ལུགས་བཟུང་གི། གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག མདོར་ན་བཤད་པའི་ཀ་ཆེན་རྣམ་བཅུ་དང་། སྒྲུབ་བརྒྱུད་ཤིང་རྟ་སྤྱི་དང་ཞི་བྱེད་སོགས། མདོ་སྔགས་ཟུང་འབྲེལ་མན་ངག་དུ་མས་ཕྱུག གངས་ལྗོངས་རྒྱལ་བསྟན་ཡུན་རིང་འབར་གྱུར་ཅིག བསྟན་འཛིན་ཞབས་བརྟན་ཐུགས་མཐུན་དགེ་འདུན་རྣམས། བསྟན་པ་འཁོར་ལོ་གསུམ་གྱིས་སྐྱོང་བ་དང་། བསྟན་ལ་དད་པའི་སྐྱེས་བུ་ས་སྟེང་ཁྱབ། རིས་མེད་རྒྱལ་བསྟན་ཡོངས་རྫོགས་འབར་གྱུར་ཅིག འཇིག་རྟེན་ཀུན་ཏུ་དམག་འཁྲུག་མུ་གེ་དང་། གདུག་རྩུབ་བསམ་སྦྱོར་མིང་ཡང་མི་གྲགས་ཤིང་། ཡིད་ཅན་བྱམས་ལྡན་སྣོད་བཅུད་དགེ་མཚན་འཕེལ། དེ་དགའ་རྒྱ་མཚོས་ཕྱོགས་ཀུན་ཁྱབ་གྱུར་ཅིག བདག་ས་ཀྱང་དེང་ནས་ཡོངས་རྫོགས་བསྟན་པའི་ལམ། རྒྱ་ཆེ་སེམས་བསྐྱེད་ཟབ་མོ་ལྟ་བ་ལ། ཐོས་བསམ་སྒོམ་པས་གཅིག་ཏུ་བརྩོན་པ་ཡིས། འཕྲལ་ཡུན་གཏན་བདེའི་ས་ལ་མྱུར་རེག་ཤོག ནམ་མཁའི་མཐར་གཏུ མཐར་གཏུགས་གྱུར་པའི་སེམས་ཅན་ཕྱིར། ཐུབ་དབང་རྒྱལ་སྲས་ཡིད་འོང་སྤྱོད་པ་ལ། ཞུམ་དང་ངལ་ཞེན་ལ་ལོ་མེད་པ་དང་། ཡིད་ཆེས་འདུན་པའི་སྤྲོ་དགས་འཇུག་པར་ཤོག བདག་གི་ལུས་དང་ལོངས་སྤྱོད་དགེ་བ་རྣམས། མ་རྣམས་བདེ་བའི་རྒྱུ་རུ་འགྱུར་བ་དང་། བདེ་དག་ཀུན་གྱི་སྡུག་ཀུན་གང་མཆིས་པ། དེ་ཀུན་བདག་ལ་ཡོངས་སུ་སྨིན་པར་ཤོག བདག་ནི་མཐོང་ཐོས་དྲན་དང་མོས་བྱེད་པ། ཀུན་ཀྱང་བདེ་དགེའི་དཔལ་ལ་སྤྱོད་པ་དང་། སྨོད་དང་རྡེག་འཚོག་མི་སྙན་སྒྲོག་བྱེད་པའང་། ཐམས་ཅད་བྱང་ཆུབ་ལམ་གྱི་སྐལ་ལྡན་ཤོག་མདོར་ན་ཇི་སྲིད་ནམ་མཁའ་གནས་པ་དང་། འགྲོ་བའི་སྡུག་བསྔལ་གནས་པ་དེ་སྲིད་དུ། བདག་ཀྱང་གནས་ཏེ་དངོས་དང་བརྒྱུད་པ་ཡིས། ཕན་བདེའི་བྱེད་པོ་ཉིད་དུ་འགྱུར་བར་ཤོག །།";
         static let DHENTSIG_MONLAM_CONTENT: String = "༈ ན་མོ་རཏྣ་ཏྲ་ཡཱ་ཡ། ཚད་མེད་ཡོན་ཏན་རྒྱ་མཚོའི་དཔལ་མངའ་ཞིང་། ཉམ་ཆུང་འགྲོ་ལ་བུ་གཅིག་ལྟར་དགོངས་པའི། དུས་གསུམ་ར་བདེ་གཤེགས་སྲས་དང་སློབ་མར་བཅས། བདག་གི་བདེན་པའི་སྨྲེ་ངག་འདིར་དགོངས་ཤིག སྲིད་ཞིའི་གདུང་སེལ་ཡོངས་རྫོགས་ཐུབ་པའི་བསྟན། འཛམ་གླིང་ཡངས་པའི་ཕན་བདེའི་དཔལ་དུ་རྒྱས། དེ་འཛིན་མཁས་དང་གྲུབ་པའི་སྐྱེས་བུ་རྣམས། ཆོས་སྤྱོད་རྣམ་བཅུའི་བྱ་བ་འཕེལ་བར་མཛོད། མི་བཟད་ལས་ངན་དྲག་པོས་ཡོངས་ནོན་པས། བར་མེད་དུཁས་མནར་བའི་ཉམ་ཐག་འགྲོ། བཟོད་དཀའ་ནད་མཚོན་མུ་གེའི་འཇིགས་པ་ཀུན། པོར་དབུགས་དབྱུང་མཛོད། ཁྱད་པར་གངས་ཞི་ནས་བདེ་དགའ་རྒྱ་མཚོར་ལྗོངས་ཆོས་ལྡན་སྐྱེ་འགྲོ་རྣམས། ནག་ཕྱོགས་ཀླ་ཀློའི་དཔུང་གིས་བརྩེ་མེད་དུ། ངན་དགུས་འཇོམས་པའི་ཁྲག་དང་མཆི་མའི་རྒྱུན། མྱུར་དུ་ཆད་པའི་ཐུགས་རྗེའི་མཐུ་དཔུང་སྐྱེད། ཉོན་མོངས་གདོན་གྱིས་སྨྱོས་པའི་བརླང་སྤྱོད་ཀྱིས། རང་གཞན་གཉིས་ཕུང་བགྱིད་པའི་སྙིང་རྗེའི་ཡུལ། མི་བསྲུན་སྐྱེ་བོའི་ཚོགས་རྣམས་བླང་དོར་མིག ཡོངས་ཐོབ་བྱམས་བརྩེས་མཛའ་བའི་དཔལ་ལ་སྦྱོར། རིང་ནས་སྙིང་དུ་བརྣག་པའི་འདོད་པའི་དོན།ཡོངས་རྫོགས་བོད་ལྗོངས་རང་དབང་གཙང་མའི་དཔལ། ལྷུན་འགྲུབ་ཆོས་སྲིད་ཟུང་འབྲེལ་དགའ་སྟོན་ལ། སྤྱོད་པའི་སྐལ་བཟང་མྱུར་བ་ཉིད་དུ་སྩོལ། བསྟན་དང་དེ་འཛིན་ཆབ་སྲིད་རང་རིགས་སླད། གཅེས་པའི་ལུས་སྲོག་ལོངས་སྤྱོད་ཡོངས་བཏང་སྟེ། དཀའ་བ་བརྒྱ་ཕྲག་སྒྲུབ་པའི་སྐྱེ་བོ་རྣམས། གྲུ་འཛིན་མགོན་དེས་ཐུགས་རྗེས་སྐྱོང་བར་མཛོད། མདོར་ན་མགོན་པོ་སྤྱན་རས་གཟིགས་དབང་གིས། སྲས་བཅས་རྒྱལ་བའི་སྤྱན་སྔར་གངས་ཅན་ཞིང་། ཡོངས་བཟུང་རྒྱ་ཆེན་སྨོན་ལམ་གང་མཛད་པའི། འབྲས་བཟང་དེང་འདིར་མྱུར་དུ་འཆར་བར་གསོལ། སྣང་སྟོང་ཆོས་ཉིད་ཟབ་མོའི་རྟེན་འབྲེལ་དང༌། མཆོག་གསུམ་ཐུགས་རྗེའི་མཐུ་དང་བདེན་ཚིག་སྟོབས། ལས་འབྲས་བསླུ་མེད་བདེན་མཐུས་བདག་ཅག་གི། བདེན་པའི་སྨོན་ལམ་གེགས་མེད་མྱུར་འགྲུབ་ཤོག ";
         static let NGOWA_MONLAM_TWO_CONTENT: String = "༈ འཇམ་དཔལ་དབའ་བོས་ཇི་ལྟར་མཁྱེན་པ་དང་། ཀུན་ཏུ་བཟང་པོ་དེ་ཡང་དེ་བཞིན་དུ། དེ་དག་ཀུན་གྱི་རྗེས་སུ་བདག་སློབ་ཅིང་། དགེ་བ་འདི་དག་ཐམས་ཅད་རབ་ཏུ་བསྔོ། །། \n" +
                 " ༈ དུས་གསུམ་གཤེགས་པའི་རྒྱལ་བ་ཐམས་ཅད་ཀྱིས། བསྔོ་བ་གང་ལ་མཆོག་ཏུ་བསྔགས་པ་དེས། བདག་གི་དགེ་བའི་རྩ་བ་འདི་ཀུན་ཀྱང་། བཟང་པོ་སྤྱོད་ཕྱིར་རབ་ཏུ་བསྔོ ་པར་བགྱི ། །། \n" +
                 " ༈ བྱང་ཆུབ་སེམས་མཆོག་རིན་པོ་ཆེ། མ་སྐྱེས་པ་རྣམས་སྐྱེ་གྱུར་ཅིག སྐྱེས་པ་ཉམས་པ་མེད་པ་ཡང་། གོང་ནས་གོང་དུ་འཕེལ་བར་ཤོག །། \n" +
                 " ༈ ཕ་མ་སེམས་ཅན་ཐམས་ཅད་བདེ་དང་ལྡན་གྱུར་ཅིག ངན་འགྲོ་ཐམས་ཅད་རྟག་ཏུ་སྟོངས་པ་དང་། བྱང་ཆུབ་སེམས་དཔའ་གང་ན་སུ་བཞུགས་པ། དེ་དག་ཀུན་གྱི་སྨོན་ལམ་འགྲུབ་གྱུར་ཅིག །། \n" +
                 "༈ ཧི། འཇམ་གླི ང་སྤྱི་དང་ཁྱད་པར་གངས་ལྗོངས་ཀྱི། གཙུག་གི་ནོར་བུ་མཐོང་བ་དོན་ལྡན་མཆོག བསྟན་འགྲོའི་བདེ་སྐྱིད་མི་ནུབ་སླར་འཕེལ་སླད། འཕྲིན་ལས་རྩིབས་སྟོང་བཀྲ་བའི་མུ་ཁྱུད་ཅན། མི་འགྱུར་རྡོ་རྗེའི་ཁྲི་ལ་བརྟན་བཞུགས་གསོལ། མཆོག་གསུམ་རྒྱལ་བ་རྒྱ་མཚོའི་བྱིན་མཐུ་དང་། འཆི་མེད་ལྷ་ཚོགས་རྣམས་ཀྱི་བདེན་པ་ཡིས། གངས་ཅན་སྐྱེ་རྒུའི་རེ་འབྲས་མི་བསླུ་བའི། སྨོན་པའི་དོན་རྣམས་ལྷུན་གྱིས་འགྲུབ་གྱུར་ཅིག ། \n" +
                 " ༈ འདི་ལྟར་བགྱིས་པའི་རྣམ་དཀར་དགེ་བའི་མཐུས། བདག་ལ་དྲིན་གྱིས་བསྐྱངས་པའི་ཕ་མ་དང་། སེམས་བསྐྱེད་ཞུས་པའི་བླ་མ་སློབ་དཔོན་དང༌། དམ་ཚིག་གཅིག་པའི་རྡོ་རྗེའི་སྤུ་རྣམས་དང་། རྒྱུ་རྫས་འབྲེལ་བ་ཡོད་པའི་མི་རྣམས་དང་། བཞོན་ཁལ་སྲོག་གཅོད་འོ་ཞོ་འཐུང་བ་དང་། ཤ་ཁྲག་ཟོས་པའི་དུད་འགྲོ་མ་ལུས་པ། མྱུར་དུ་སངས་རྒྱས་གོ་འཕང་ཐོབ་པར་ཤོག ། \n" +
                 " ༈ སྨོན་ལམ་ལོག་པའི་༧རྒྱལ་བའི་བསྟན་པ་ལ། འཁུ་བའི་ནག་ཕྱོགས་གཟུགས་ཅན་གཟུགས་མེད་སྡེ། བསམ་སྦྱོར་ངན་པའི་འཇུག་པ་མ་ལུས་པ། མཆོག་གསུམ་བདེན་པས་རྩད་ནས་གཅོད་གྱུར་ཅིག །";
         static let PHAKTOT_CONTENT: String = "༈ ན་མོ་ཨཱརྱ་ལོ་ཀེ་ཤྭ་རཱ་ཡ། །ཕྱོགས་བཅུའི་རྒྱལ་བ་རབ་འབྱམས་ཞིང་གི་རྡུལ་དང་མཉམ་པ་དེ་དག་གི། །འགྲོ་ལ་ཆགས་པའི་ཀུན་ཏུ་ཆགས་པའི་སྙིང་རྗེ་རྒྱ་མཚོ་ལྟ་བུར་ཟབ། །དེ་ལས་འཁྲུངས་པའི་ཡོན་ཏན་ལྷུན་རྫོགས་སྤྱན་རས་གཟིགས་ཞེས་རྣམ་གྲགས་པ། །མགོན་པོ་བདག་ཅག་ཀུན་གྱིས་བསྟེན་འོས་རིན་ཆེན་རི་འདྲ་དེ་ལ་འདུད། །དྲི་མེད་ཆུ་སྐྱེས་གསར་པའི་དབུས་སུ་བསིལ་ཟེར་གང་བའི་དཀྱིལ་འཁོར་སྟེང༌། །པདྨའི་འདབ་ལྟར་རྣམ་པར་དག་པའི་ཞབས་གཉིས་མཉམ་པའི་ཚུལ་བཞུགས་ཏེ། །འཇིག་རྟེན་དག་ན་ཟླ་བ་ཉ་འདྲ་ཀུན་ཏུ་ལྷམ་མེར་གསལ་བ་ཁྱོད། །མཐོང་བའི་མོད་ལ་སྙིང་གི་གདུང་སེལ་ཀུན་ནས་དགའ་བ་རྒྱས་པར་མཛད། །ཁ་བས་རེག་པ་ལྟ་བུར་བསིལ་ཡང་བདུད་ཀྱི་སྡེ་རྣམས་ལྷག་པར་བསྲེག །མཐའ་དག་མཚན་མའི་ཆོས་ལས་འདས་ཀྱང་སྤྲོས་པ་དུ་མའི་དཀྱིལ་འཁོར་སྣང༌། །སྲིད་ལས་བརྒལ་ཡང་སྲིད་པ་གསུམ་དུ་སྐྱེ་བ་རྟག་ཏུ་སྟོན་མཛད་པའི། །སྒྱུ་མ་ཇི་བཞིན་ཁྱོད་ཀྱི་སྤྱོད་ལ་བློ་དང་ལྡན་རྣམས་ངོ་མཚར་འགྱུར། །རྣམ་པ་ཀུན་གྱི་རྗེས་སུ་ཞུགས་པ་སྟོན་མཚན་ནམ་མཁའི་དཀྱིལ་འཁོར་བཞིན། །སྐལ་ལྡན་སྐྱེ་བོས་རྟག་ཏུ་བསྟེན་པས་སྐྱོ་མེད་ས་གཞི་ཉིད་དུ་གྱུར། །འཇིག་རྟེན་ཡོངས་ཀྱི་གཏི་མུག་མུན་པ་མཐར་མཛད་ཉི་མ་འབར་བ་དང། །ཞེ་སྡང་ཡིད་བསྲེག་གདུང་བ་སེལ་བའི་རི་བོང་མཚན་མ་འཛིན་པ་ཅན། །པུཎྜ་རི་ཀ་རབ་ཏུ་བཞད་པའི་ཞལ་གྱི་དཀྱིལ་འཁོར་མཛེས་སྟོན་ཅིང༌། །དྲི་མེད་དུང་དང་ཆུ་ཤེལ་ལྟར་དཀར་ལྟ་ན་སྡུག་པའི་ལང་ཚོ་ཅན། །བསམ་ཡས་མཆོད་སྤྲིན་རྒྱས་པའི་དབུས་ན་སྒྲོལ་མཛད་སྒྱུ་མའི་རྩེད་འཇོ་དང༌། །བདེ་གཤེགས་རྒྱལ་སྲས་ཁྲོ་བོའི་དཔུང་སོགས་གྲངས་མེད་འཁོར་གྱི་དབུས་ན་བརྗིད། །མེ་དང་ཆུ་ཀླུང་སྐལ་བ་ངན་དང་རྒྱལ་པོའི་འཇིགས་པ་ལ་སོགས་པ། །ཤིན་ཏུ་ཉམ་ཐག་གནས་སུ་ལྷུང་བའི་སྡུག་བསྔལ་བརྒྱ་ཡིས་གཙེས་པ་ཀུན། །ཁྱོད་ཀྱི་འོད་ཟེར་ཟླ་བ་བཀྲུས་འདྲའི་ལུ་གུ་རྒྱུད་ཀྱིས་ཉེར་དྲངས་ནས། །ལྷ་ཡི་དཔལ་དང་རབ་ཏུ་ལྡན་པའི་བདེ་བ་མཆོག་ལ་མངོན་པར་སྦྱར། །དྲི་ཞིམ་ལན་བུ་སྤྱི་བོར་བཅིངས་པ་ནམ་མཁའི་འོད་དང་རབ་འདྲེས་ལ། །རྒྱལ་བའི་དབང་པོ་འོད་དཔག་མེད་སྐུ་རིགས་ཀྱི་ཅོད་པན་དབུ་ལ་མཛེས། །ཕྱག་གཡས་མཆོག་སྦྱིན་རྩེ་མོ་ལས་བབས་བདུད་རྩི་དཀར་པོའི་ཐིགས་པ་ནི། །མུ་ཏིག་འོད་ཆགས་རྟག་ཏུ་ལྷུང་བས་ཡི་དྭགས་ཀུན་གྱི་གདུང་བ་འཇོམས། །ཕྱག་གཡོན་སྟ་ཟུར་ངོས་ལ་བརྟེན་པའི་བགྱིང་བག་ཡིད་འོང་གཙང་མའི་སྐུ། །ཚངས་དབང་ལ་སོགས་དྲེགས་ལྡན་འདུད་པས་ཁྱོད་ནི་འཇིག་རྟེན་ཀུན་དབང་ཕྱུག །འཁོར་ལོ་པདྨ་མདའ་དང་གཞུ་ཡི་ཕྱག་རྒྱ་སོགས་ཀྱིས་རབ་སྤ་བའི། །སྟོང་ཕྲག་ཕྱག་དང་སྤྱན་སྟོང་མངའ་བ་བཅུ་གཅིག་ཞལ་བཟང་ཡང་དག་སྟོན། །ལ་ལར་གདུག་ཅན་ཀླུ་ཡི་དེད་དཔོན་སེང་གེའི་སྒྲ་དང་གཞན་དུ་ནི། །དོན་ཡོད་ཞགས་དང་མགྲིན་སྔོན་ལ་སོགས་རྣམ་པ་དུ་མའི་ཆོ་འཕྲུལ་འགྱེད། །རེས་འགའ་བགེགས་འཇོམས་ཁྲོ་རྒྱལ་རྟ་མགྲིན་ལེགས་ལྡན་ནག་པོའི་ཚུལ་ལ་སོགས། །དྲག་ཤུལ་ས་གསུམ་འགས་པ་སྟོན་ཡང་ཐུགས་ནི་བརྩེ་ལས་གཡོ་མི་མངའ། །ནམ་མཁའི་ལམ་གྱི་རྗེས་སུ་སོང་བའི་འཇིག་རྟེན་རྒྱ་ཆེན་མཐའ་མ་མཆིས། །དེ་ལྟ་མོད་ཀྱི་སེམས་ཅན་གྲངས་མཚུངས་ཁྱོད་ཀྱི་སྤྲུལ་པ་དེ་སྙེད་ཅིག །རབ་ཏུ་སྤྲོ་ཡང་བ་སྤུའི་རྩེ་ཙམ་གཡོ་སྙམ་བགྱིད་པ་མ་ཡིན་ཏེ། །ཆར་སྤྲིན་ལས་ནི་ཆུ་ཆར་བཞིན་དུ་འབབ་པའི་རྟོག་པ་མངའ་མ་ལགས། །རྣམ་པ་འགར་ཡང་འགྲོ་བའི་དོན་དུ་མི་འགྱུར་ཚིག་ནི་ཡོངས་སུ་སྤངས། །རང་རང་བློ་དང་རྗེས་སུ་མཐུན་པའི་འགག་མེད་དབྱངས་ཀྱི་ཡན་ལག་ནི། །རྒྱ་མཚོའི་རྡུལ་གྱི་གྲངས་ལས་འདས་པའི་ཆོས་ལམ་ཉེ་བར་སྟོན་མཛད་པ། །ཁྱོད་གསུང་སུ་ཡི་རྣ་བར་ལྷུང་ཡང་བཀའ་ལ་ཡང་དག་གནས་པར་འགྱུར། །དྲི་ཟའི་རྒྱུད་མང་སྒྲ་དང་འཐབ་པའི་ཡིད་འོང་ན་ཆུང་གླུ་ལྟ་བུ། །ཡིད་དུ་འཐད་ཀྱང་ཆགས་ལྡན་རྣམས་ཀྱི་ཡུལ་གྱི་འདོད་པ་མ་ལུས་འཇོམས། །ནམ་མཁའ་ཁྱབ་པའི་འབྲུག་སྒྲ་ཇི་བཞིན་ཟབ་མོར་འབྱིན་ཡང་དེ་ཡི་མོད། །འཇིགས་པས་སྐྲག་པ་དེ་དག་གི་ཡང་སྡུག་བསྔལ་མིང་གི་ལྷག་མར་མཛད། །འཁོར་བ་པ་རྣམས་རྟག་ཏུ་གཡོ་བའི་སྐྱེ་བ་ཡང་དག་གྲོལ་སླད་དུ། །རྣམ་པ་ཀུན་ལྡན་གསང་མཐོར་སྒྲོག་པའི་ཆོས་ཀྱི་འཁོར་ལོ་སྔོན་འགྲོ་བ། །དུས་གསུམ་གཏོགས་པའི་རྒྱལ་བ་ཀུན་གྱི་གསུང་དབྱངས་ངེས་པ་རབ་རྫོགས་པས། །བླ་མེད་འབྱོར་བའི་དབང་ཕྱུག་ཁྱོད་ནི་འཇིག་རྟེན་ཀུན་གྱི་བླ་མར་གྲགས། །སྐྱེ་བོ་གང་དག་རང་སྡུག་ཉིད་ཀྱང་རེས་འགའ་ཁྲོ་བོའི་དབང་གྱུར་ཏེ། །གསོད་པར་བརྩམས་མོད་ཁྱོད་ཀྱིས་འགྲོ་རྣམས་བུ་དང་འདྲ་བར་རྟག་དགོངས་ནས། །གནོད་འགྱུར་ཆ་ཙམ་ཐུགས་ཀྱིས་དགོངས་པ་རིང་མོ་ཞིག་ནས་བཏང་བས་ན། །བདག་འདྲ་རང་ལ་བརྩེ་བ་དེས་ཀྱང་ཁྱོད་ཐུགས་སྙིང་རྗེའི་ཆར་མི་ཕོད། །ག་བུར་ཙན་ལྡན་ཨུ་ཤིར་ལ་སོགས་ཆབ་ཀྱིས་སྦངས་པ་ལྟར་བསིལ་བའི། །ཞི་བའི་གཟིགས་སྟངས་ནམ་ཡང་མི་འཛུམ་རིགས་དྲུག་སྤྱན་རས་བཙའ་མོད་ཀྱི། །མྱོང་སྣང་སྤྲོས་པ་ཆོས་ཀྱི་དབྱིངས་སུ་ཉེ་བར་ཞི་བའི་དགོངས་པ་ནི། །བདེ་བ་མཆོག་གིས་རྟག་ཏུ་འཁྱུད་བཞིན་གཡོ་བ་མེད་ལ་ཕྱག་འཚལ་ལོ། །ཕྱོགས་ཀུན་འབར་བའི་མེ་ལྕེས་བསྲེག་ཅིང་ཁ་གདངས་མིག་རྩ་དམར་ཁྲོས་པའི། །གཤེད་མས་ཁོར་ཡུག་ཀུན་ནས་འཇིགས་བསྐྱེད་ལ་ལ་ཟངས་ཞུན་འབར་བའི་ཁྲོད། །འབྲུ་ལྟར་འཚོད་པའི་ཚ་དམྱལ་གནས་སུ་བདུད་རྩིའི་ཆར་གྱིས་བསིལ་བ་དང༌། །ཤ་རུས་འགས་བྱེད་གྲང་བས་ཉེན་རྣམས་དྲོ་བའི་རེག་པས་སྐྱོབ་ཏུ་གསོལ། །ལྟོ་བ་རི་མཚུངས་མགྲིན་པ་འགག་ཅིང་ཡན་ལག་རྩ་ཕྲན་བཞིན་དུ་ཕྲ། །ལུས་སྐམས་ཐལ་བས་རབ་གཡོགས་ལྷུ་ཚིགས་འཐབ་ལས་མེ་ཡི་ཚ་ཚ་འབབ། །ཆུ་ཀླུང་དག་ཀྱང་བལྟས་པ་ཙམ་གྱིས་སྐེམ་འགྱུར་རྟག་ཏུ་ངལ་བ་དང༌། །བཀྲེས་པས་གདུང་བའི་ཡི་དྭགས་རྣམས་ལ་ཟས་གོས་བདུད་རྩིའི་ཆར་པ་སྩོལ། །ཟས་ཁམ་ཙམ་གྱི་འདོད་ལ་སྲེད་པས་ཕྲ་མོའི་བདེ་བ་དེ་ཡི་སླད། །གཅེས་པའི་སྲོག་ཀྱང་ཡལ་བར་འདོར་བགྱིད་བླང་དོར་འགའ་ཡང་སྐབས་མི་ཕྱེད། །དབྱིབས་དང་ཁ་དོག་མི་སྡུག་ལ་བརྟེན་ཤིན་ཏུ་སྐལ་དམན་དུད་འགྲོ་རྣམས། །མྱུར་དུ་དགེ་བའི་དྲན་པ་ཐོབ་ཅིང་བདེ་བས་འཚོ་བ་སྩལ་དུ་གསོལ། །ནོར་བུ་འབར་བའི་གཞལ་མེད་ལ་བརྟེན་ཚངས་པའི་རྒྱལ་པོའི་དཔལ་དེ་ཡང༌། །མི་རྟག་ཉམས་ནས་མནར་མེད་མེ་ནང་རུས་པ་རྐང་ཡང་འབར་འགྱུར་ཞིང༌། །རེག་ན་བདེ་བའི་མལ་སྟན་འཇམ་དང་ཡིད་འོང་སྐྱེད་ཚལ་ངོགས་རྩེ་བའི། །ལྷ་བུ་ཉིད་ཀྱང་འགྱུར་བྱེད་འཐུང་ཞིང་ལྕགས་ཀྱི་ཚེར་རྣོན་ཐང་ལ་འདྲད། །མཐོ་བ་ཉིད་ཀྱང་མི་རིང་དུས་སུ་རྐང་པས་བསྣུན་འོས་བྲན་དུ་འགྱུར། །ལང་ཚོ་འདི་ཡང་ཡུད་ཀྱིས་ཉམས་འགྱུར་སྟོན་མཐའི་མེ་ཏོག་ཇི་ལྟ་བུ། །འབྱོར་བ་འདི་ཡང་དུས་ཀྱིས་འདོར་ངེས་བརྙན་པོའི་རྒྱན་ལ་དགའ་སྙམ་བཞིན། །བདག་གི་ཚེ་ཡང་གློག་འོད་འགྱུ་བཞིན་སྐད་གཅིག་ཙམ་ཞིག་འདིར་སྣང་སྟེ། །འཇིག་རྟེན་འདི་ཡི་ཕ་རོལ་ཞེས་བྱ་སྔར་འདྲིས་དངོས་པོ་ཐག་བསྲིང་བའི། །མུན་ཁྲོད་སྟུག་པོར་བདག་ཉིད་གཅིག་པུ་རྣམ་པར་འཁྱམས་འགྱུར་དེ་ཡི་སླད། །འཇིགས་རུང་འཁོར་བར་ཉོན་མོངས་སྒྱུ་ཡིས་སྙིང་ལ་མངོན་པར་བསླུས་གྱུར་པ། །བདག་ཅག་སྡུག་བསྔལ་རྒྱ་མཚོ་འདི་ནས་ཐུགས་རྗེའི་ཕྱག་གིས་དྲང་དུ་གསོལ། །གངས་ཅན་རི་ཡིས་བསྐོར་བའི་ཞིང་འདིར་ཐུབ་པའི་བསྟན་སྲོལ་གནས་མོད་ཀྱི། །ཆོས་བྱེད་ཕལ་ཆེར་ཚུལ་ཁྲིམས་གོས་བྲལ་གཅེར་བུ་ངོ་ཚ་སྤངས་ནས་ནི། །ཁྱིམ་པ་རྣམས་ལ་བསྙེན་ཅིང་ངོར་བལྟ་དམ་པས་སྨད་པའི་ལས་ལ་འཇུག །ཀྱེ་མ་དམ་ཆོས་ཉི་མའི་འོད་ཟེར་རི་བོའི་ཕྲག་ཏུ་ནུབ་ལ་ཉེ། །སྟོབས་དང་ལྡན་རྣམས་རང་འདོད་ལྷུར་ལེན་ཕན་ཚུན་རྩོད་ཅིང་དམའ་ལ་འཚེ། །དམན་པ་རྣམས་ནི་རྟག་ཏུ་འཁོལ་ཞིང་གྱི་ནའི་ཟས་ཀྱང་རྙེད་པར་དཀའ། །མི་བཟད་སྙིགས་མའི་ངང་ཚུལ་འདི་འདྲར་འཕགས་པས་བཏང་སྙོམས་མཛད་ལགས་ན། །སྔོན་གྱི་ཐུགས་དམ་དེ་ཡིས་ཅི་འཚལ་འབངས་རྣམས་སུ་ཞིག་སྐྱབས་ལ་བསྟེན། །ཁྱོད་ཀྱི་མཚན་ཡང་བྱིན་གྱིས་བརླབས་ཏེ་གང་ཐོས་འཇིགས་པ་ཞི་བ་དང༌། །སངས་རྒྱས་གྲངས་མེད་བསྙེན་པའི་ལས་དང་ཁྱོད་མཚན་འཛིན་ཙམ་འབྲས་མཚུངས་ཞེས། །དྲང་སྲོང་བདེན་པའི་ཚིག་གིས་བསྟན་བཞིན་བདག་ཅག་རེ་བས་གསོལ་འདེབས་ན། །མགོན་པོ་གཞན་དུ་གཡེལ་བའི་ཐུགས་སྤངས་ཉམ་ཐག་སེམས་ཅན་སྐྱོབ་ཏུ་གསོལ། །སྐྱེ་བོ་ཐམས་ཅད་དཀོན་མཆོག་གསུམ་པོ་གུས་པས་ཉེ་བར་བསྟེན་པ་དང༌། །ལས་དང་འབྲས་བུའི་གནས་ལ་དད་པས་རྟག་ཏུ་ཆོས་ཀྱི་ལམ་དུ་འཇུག །སྣ་ཚོགས་ཁམས་དང་སྐལ་བར་འཚམས་པའི་ཞི་ཁྲོའི་ཆོ་འཕྲུལ་དཔག་མེད་ཀྱིས། །སྲིད་པ་ཉམ་ངའི་གནས་འདིར་ལྷུང་བའི་གཉེན་རྣམས་བདེ་ལ་སྦྱོར་དུ་གསོལ། །རབ་གུས་བདག་ཀྱང་དེ་ཉིད་འབྱེད་པའི་བློ་གྲོས་དྲི་མ་མེད་པ་དང༌། །སྲིད་ལ་སྐྱོ་དང་སྙིང་རྗེས་གཞན་དོན་སྐྱེ་བ་དུ་མར་སྤྱོད་ལ་བརྩོན། །བདུད་སྡེ་མཐའ་དག་ཕམ་པར་བྱེད་པའི་མཐུ་ཆེན་ཕ་རོལ་ཕྱིན་ཐོབ་སྟེ། །རྣམ་དག་དགའ་ལྡན་ཆོས་ཀྱི་ཚུལ་དང་འགྲོ་བ་དུས་རིང་སྐྱོང་གྱུར་ཅིག །བློ་གྲོས་དམན་ཡང་ཁོ་བོའི་ཡིད་ཀྱི་འོ་མཚོ་གྱེན་དུ་འཕྱུར་བའི་མཐུས། །བསྟོད་ཅིང་སྨྲེ་བའི་འཕྲེང་བ་རབ་མཛེས་དབུ་བ་དཀར་པོ་རྒོད་པ་འདི། །ཀློག་གམ་ཐོས་སམ་དྲན་པ་དེ་ཡང་སྡིག་པའི་དྲི་མ་རྣམ་དག་ཅིང༌། །གྲུ་འཛིན་ཞིང་དུ་རྒྱལ་བ་མཆོག་རབ་སྤྱན་རས་གཟིགས་ཀྱིས་རྗེས་བཟུང་ཤོག །";
         static let KYABDO_CONTENT: String = "༈ ཕྱོགས་བཅུ་དུས་གསུམ་གྱི་དེ་བཞིན་གཤེགས་པ་ཐམས་ཅད་ཀྱི་སྐུ་གསུང་ཐུགས་ཡོན་ཏན་འཕྲིན་ལས་ཐམས་ཅད་ཀྱི་ངོ་བོར་གྱུར་པ། །ཆོས་ཀྱི་ཕུང་པོ་སྟོང་ཕྲག་བརྒྱད་ཅུ་རྩ་བཞིའི་འབྱུང་གནས་འཕགས་པའི་དགེ་འདུན་ཐམས་ཅད་ཀྱི་མངའ་བདག། །དྲིན་ཅན་རྩ་བ་དང་བགྱུད་པར་བཅས་པའི་དཔལ་ལྡན་བླ་མ་དམ་པ་རྣམས་ལ་སྐྱབས་སུ་མཆིའོ། །བླ་མ་ལ་སྐྱབས་སུ་མཆིའོ། །སངས་རྒྱས་ལ་སྐྱབས་སུ་མཆིའོ། །ཆོས་ལ་སྐྱབས་སུ་མཆིའོ། །དགེ་འདུན་ལ་སྐྱབས་སུ་མཆིའོ། །ཡི་དམ་དཀྱིལ་འཁོར་གྱི་ལྷ་ཚོགས་འཁོར་དང་བཅས་པ་རྣམས་ལ་སྐྱབས་སུ་མཆིའོ། །དཔལ་མགོན་དམ་པ་ཆོས་སྐྱོང་བའི་སྲུང་མ་ཡེ་ཤེས་ཀྱི་སྤྱན་དང་ལྡན་པ་རྣམས་ལ་སྐྱབས་སུ་མཆིའོ། ། སངས་རྒྱས་ཆོས་དང་ཚོགས་ཀྱི་མཆོག་རྣམས་ལ། །བྱང་ཆུབ་བར་དུ་བདག་ནི་སྐྱབས་སུ་མཆི། ། བདག་གིས་སྦྱིན་སོགས་བགྱིས་པ་ཚོགས་རྣམས་ཀྱིས། །འགྲོ་ལ་ཕན་ཕྱིར་སངས་རྒྱས་འགྲུབ་པར་ཤོག། །དཀོན་མཆོག་གསུམ་ལ་བདག་སྐྱབས་མཆི། །སྡིག་པ་ཐམས་ཅད་སོ་སོར་བཤགས། །འགྲོ་བའི་དགེ་ལ་རྗེས་ཡི་རང་། །སངས་རྒྱས་བྱང་ཆུབ་ཡིད་ཀྱིས་གཟུང་། །སངས་རྒྱས་ཆོས་དང་ཚོགས་མཆོག་ལ། །བྱང་ཆུབ་བར་དུ་བདག་སྐྱབས་མཆི། །རང་གཞན་དོན་ནི་རབ་སྒྲུབ་ཕྱིར། །བྱང་ཆུབ་སེམས་ནི་བསྐྱེད་པར་བགྱི། །བྱང་ཆུབ་མཆོག་གི་སེམས་ནི་བསྐྱེད་བགྱིས་ནས། །སེམས་ཅན་ཐམས་ཅད་བདག་གིས་མགྲོན་དུ་གཉེར། །བྱང་ཆུབ་སྤྱོད་མཆོག་ཡིད་འོང་སྤྱད་པར་བགྱི། །འགྲོ་ལ་ཕན་ཕྱིར་སངས་རྒྱས་འགྲུབ་པར་ཤོག། །སེམས་ཅན་ཐམས་ཅད་བདེ་བ་དང་བདེ་བའི་རྒྱུ་དང་ལྡན་པར་གྱུར་ཅིག། །སྡུག་བསྔལ་དང་སྡུག་བསྔལ་གྱི་རྒྱུ་དང་བྲལ་བར་གྱུར་ཅིག། །སྡུག་བསྔལ་མེད་པའི་བདེ་བ་དང་མི་འབྲལ་བར་གྱུར་ཅིག། །ཉེ་རིང་ཆགས་སྡང་དང་བྲལ་བའི་བཏང་སྙོམས་ལ་གནས་པར་གྱུར་ཅིག། ༈ གང་གིས་རྟེན་ཅིང་འབྲེལ་བར་འབྱུང་། །འགག་པ་མེད་པ་སྐྱེ་མེད་པ། ། ཆད་པ་མེད་པ་རྟག་མེད་པ། །འོང་བ་མེད་པ་འགྲོ་མེད་པ། །ཐ་དད་དོན་མིན་དོན་གཅིག་མིན། །སྤྲོས་པ་ཉེར་ཞི་ཞི་བསྟན་པ། ། རྫོགས་པའི་སངས་རྒྱས་སྨ་རྣམས་ཀྱི། །དམ་པ་དེ་ལ་ཕྱག་འཚལ་ལོ། ༈ ཉན་ཐོས་ཞི་བ་ཚོལ་རྣམས་ཀུན་ཤེས་ཉིད་ཀྱིས་ཉེར་ཞིར་ཁྲིད་མཛད་གང་ཡིན་དང་། །འགྲོ་ལ་ཕན་པར་བྱེད་རྣམས་ལམ་ཤེས་ཉིད་ཀྱིས་འཇིག་རྟེན་དོན་སྒྲུབ་མཛད་པ་གང་། །གང་དང་ཡང་དག་ལྡན་པས་ཐུབ་རྣམས་རྣམ་པ་ཀུན་ལྡན་སྣ་ཚོགས་འདི་གསུངས་པ། །ཉན་ཐོས་བྱང་ཆུབ་སེམས་དཔའི་ཚོགས་བཅས་སངས་རྒྱས་ཀྱི་ནི་ཡུམ་དེ་ལ་ཕྱག་འཚལ་ལོ། ༈ རྟོག་པའི་དྲ་བ་རྣམས་སེལ་ཅིང་། །ཟབ་ཅིང་རྒྱ་ཆེའི་སྐུ་མངའ་བ། །ཀུན་ཏུ་བཟང་པོའི་འོད་ཟེར་དག། །ཀུན་ནས་འཕྲོ་ལ་ཕྱག་འཚལ་ལོ། ༈ ཐམས་ཅད་དུ་ནི་ས་གཞི་དག། །གསེག་མ་ལ་སོགས་མེད་པ་དང་། །ལག་མཐིལ་ལྟར་མཉམ་བེཌཱུརྱའི། །རང་བཞིན་འཇམ་པོར་གནས་གྱུར་ཅིག། ༈ ༀ་ན་མོ་བྷ་ག་ཝ་ཏེ། །བཛྲ་སཱ་ར་པྲ་མརྡ་ནི། །ཏ་ཐཱ་ག་ཏ་ཡ། །ཨརྷ་ཏེ། །སམྱཀྶཾ་བུདྡྷྰ་ཡ། །ཏདྱ་ཐཱ། །ༀ་བཛྲེ་བཛྲེ། །མ་ཧཱ་བཛྲེ། །མཧཱ་ཏེ་ཛ་བཛྲེ། །མཧཱ་བིདྱཱ་བཛྲེ། །མཧཱ་བོ་དྷི་ཙིཏྟ་བཛྲེ། །མཧཱ་བོ་དྷི་མཎྜོ་པ་སཾ་ཀྲ་མ་ཎ་བཛྲེ་སརྦ་གརྨ་ཨཱ་བ་ར་ཎ་བི་ཤོ་དྷ་ན་བཛྲེ་སྭཱཧཱ།";
         static let BARCHAT_LAMSEL_CONTENT: String = "༄༅། །གསོལ་འདེབས་བར་ཆད་ལམ་སེལ་ནི། ། ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ ཆོས་སྐུ་སྣང་བ་མཐའ་ཡས་ལ་གསོལ་བ་འདེབས༔ ལོངས་སྐུ་ཐུགས་རྗེ་ཆེན་པོ་ལ་གསོལ་བ་འདེབས༔ སྤྲུལ་སྐུ་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས༔ བདག་གི་བླ་མ་ངོ་མཚར་སྤྲུལ་པའི་སྐུ༔ རྒྱ་གར་ཡུལ་དུ་སྐུ་འཁྲུངས་ཐོས་བསམ་མཛད༔ བོད་ཡུལ་དབུས་སུ་ཞལ་བྱོན་དྲེགས་པ་བཏུལ༔ ཨོ་རྒྱན་ཡུལ་དུ་སྐུ་བཞུགས་འགྲོ་དོན་མཛད༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ སྐུ་ཡི་ངོ་མཚར་མཐོང་བའི་ཚེ༔ གཡས་པས་རལ་གྲིའི་ཕྱག་རྒྱ་མཛད༔ གཡོན་པས་འགུགས་པའི་ཕྱག་རྒྱ་མཛད༔ ཞལ་བགྲད་མཆེ་གཙིགས་གྱེན་ལ་གཟིགས༔ རྒྱལ་བའི་གདུང་འཛིན་འགྲོ་བའི་མགོན༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ དམ་ཆོས་རིན་ཆེན་གསན་པའི་ཚེ༔ སྐུ་གསལ་འོད་ཟེར་མདངས་དང་ལྡན༔ ཕྱག་གཡས་སྡེ་སྣོད་གླེགས་བམ་བསྣམས༔ གཡོན་པས་ཕུར་པའི་པུསྟི་བསྣམས༔ ཟབ་མོའི་ཆོས་རྣམས་ཐུགས་སུ་ཆུད༔ ཡང་ལེ་ཤོད་ཀྱི་པཎྜི་ཏ༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ དམ་ཅན་དམ་ལ་བཏགས་པའི་ཚེ༔ དྲི་མེད་གནས་མཆོག་ཉམས་རེ་དགའ༔ རྒྱ་གར་བལ་ཡུལ་ས་མཚམས་སུ༔ བྱིན་གྱིས་བརླབས་ནས་བྱོན་པའི་ཚེ༔ དྲི་བསུང་སྤོས་ངད་ལྡན་པའི་རི༔ མེ་ཏོག་པདྨ་དགུན་ཡང་སྐྱེ༔ ཆུ་མིག་བྱང་ཆུབ་བདུད་རྩིའི་ཆུ༔ བདེ་ལྡན་དེ་ཡི་གནས་མཆོག་ཏུ༔ སྐྱེས་མཆོག་ཚུལ་བཟང་ཆོས་གོས་གསོལ༔ ཕྱག་གཡས་རྡོ་རྗེ་རྩེ་དགུ་བསྣམས༔ གཡོན་པས་རིན་ཆེན་ཟ་མ་ཏོག༔ རཀྟ་བདུད་རྩིས་ནང་དུ་གཏམས༔ མཁའ་འགྲོ་དམ་ཅན་དམ་ལ་བཏགས༔ ཡི་དམ་ཞལ་གཟིགས་དངོས་གྲུབ་བརྙེས༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ རྒྱལ་བའི་བསྟན་པ་བཙུགས་པའི་ཚེ༔ གཡའ་རིའི་ནགས་ལ་སྒྲུབ་པ་མཛད༔ བསྙེན་ཕུར་ནམ་མཁའི་དབྱིངས་སུ་འཕར༔ རྡོ་རྗེའི་ཕྱག་རྒྱས་བླངས་ཤིང་བསྒྲིལ༔ བསྒྲིལ་ཞིང་ཙནྡན་ནགས་སུ་འཕང་༔ མེ་འབར་འཁྲུགས་ཤིང་མཚོ་ཡང་སྐེམ༔ སྲིབ་ཀྱི་མུ་སྟེགས་ས་གང་བསྲེགས༔ ཡཀྴ་ནག་པོ་རྡུལ་དུ་བརླག༔ འགྲན་གྱི་དོ་མེད་བདུད་ཀྱི་གཤེད༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ སྲིན་པོའི་ཁ་གནོན་མཛད་པའི་ཚེ༔ ཁྱེའུ་ཆུང་སྤྲུལ་སྐུའི་ཆ་ལུགས་ཅན༔ ཡ་མཚན་གཟུགས་བཟང་ཁ་དོག་ལེགས༔ ཚེམས་འགྲིགས་དབུ་སྐྲ་སེར་ལ་མཛེས༔ དགུང་ལོ་བཅུ་དྲུག་ལོན་པའི་ཚུལ༔ རིན་ཆེན་རྒྱན་ཆ་སྣ་ཚོགས་གསོལ༔ ཕྱག་གཡས་འཁར་བའི་ཕུར་པ་བསྣམས༔ བདུད་དང་སྲིན་པོའི་ཁ་གནོན་མཛད༔ གཡོན་པས་སེང་ལྡེང་ཕུར་པ་བསྣམས༔ མོས་པའི་བུ་ལ་སྲུང་སྐྱོབ་མཛད༔ མགུལ་ན་ལྕགས་ཀྱི་ཕུར་པ་བསྣམས༔ ཡི་དམ་ལྷ་དང་གཉིས་སུ་མེད༔ གཉིས་མེད་སྤྲུལ་སྐུ་འཛམ་གླིང་རྒྱན༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ འདྲེ་ཡི་ཡུལ་དུ་དགོངས་པའི་ཚེ༔ མེ་དཔུང་ཤོད་ཀྱི་ས་གཞི་ལ༔ མདའ་རྒྱང་གང་གི་མཚོ་ནང་དུ༔ པདྨའི་སྟེང་དུ་བསིལ་བསིལ་འདྲ༔ པདྨའི་ནང་ན་དགོངས་པ་མཛད༔ མཚན་ཡང་པདྨ་འབྱུང་གནས་ཞེས༔ རྫོགས་པའི་སངས་རྒྱས་དངོས་སུ་བྱོན༔ དེ་འདྲའི་སྤྲུལ་སྐུ་ཡ་མཚན་ཅན༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ བོད་ཀྱི་ཉི་མ་མཛད་པའི་ཚེ༔ དད་ལྡན་འགྲོ་བ་འདྲེན་པའི་དཔལ༔ གང་ལ་གང་འདུལ་སྐུར་བསྟན་ནས༔ གཙང་ཁ་ལ་ཡི་ལ་ཐོག་ཏུ༔ དགྲ་ལྷའི་དགེ་བསྙེན་དམ་ལ་བཏགས༔ ཡུལ་ནི་ཚ་བའི་ཚ་ཤོད་དུ༔ ལྷ་ཡི་དགེ་བསྙེན་དྲེགས་པ་ཅན༔ ཉི་ཤུ་རྩ་གཅིག་དམ་ལ་བཏགས༔ མང་ཡུལ་དེ་ཡི་བྱམས་སྤྲིན་དུ༔ དགེ་སློང་བཞི་ལ་དངོས་གྲུབ་གནང་༔ ཁྱད་པར་འཕགས་པའི་རིག་འཛིན་མཆོག༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ དཔལ་མོ་ཐང་གི་དཔལ་ཐང་དུ༔ བརྟན་མ་བཅུ་གཉིས་དམ་ལ་བཏགས༔ བོད་ཡུལ་ཁ་ལའི་ལ་ཐོག་ཏུ༔ གངས་དཀར་ཤ་མེད་དམ་ལ་བཏགས༔ འདམ་ཤོད་ལྷ་བུའི་སྙིང་དྲུང་དུ༔ ཐང་ལྷ་ཡར་ཞུད་དམ་ལ་བཏགས༔ ཧས་པོ་རི་ཡི་ཡང་གོང་དུ༔ ལྷ་སྲིན་ཐམས་ཅད་དམ་ལ་བཏགས༔ ཆེ་བའི་ལྷ་འདྲེ་ཐམས་ཅད་ཀྱིས༔ ལ་ལས་སྲོག་གི་སྙིང་པོ་ཕུལ༔ ལ་ལས་བསྟན་པ་བསྲུང་བར་བྱས༔ ལ་ལས་བྲན་དུ་ཁས་བླངས་བྱས༔ མཐུ་དང་རྫུ་འཕྲུལ་སྟོབས་པོ་ཆེ༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ དམ་པ་ཆོས་ཀྱི་བསྟན་པ་ནི༔ རྒྱལ་མཚན་ལྟ་བུར་བཙུགས་པའི་ཚེ༔ བསམ་ཡས་མ་བཞེངས་ལྷུན་གྱིས་གྲུབ༔ རྒྱལ་པོའི་དགོངས་པ་མཐར་ཕྱིན་མཛད༔ སྐྱེས་མཆོག་གསུམ་གྱི་མཚན་ཡང་གསོལ༔ གཅིག་ནི་པདྨ་འབྱུང་གནས་ཞེས༔ གཅིག་ནི་པདྨ་སམྦྷ་ཝ༔ གཅིག་ནི་མཚོ་སྐྱེས་རྡོ་རྗེ་ཞེས༔ གསང་མཚན་རྡོ་རྗེ་དྲག་པོ་རྩལ༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ བསམ་ཡས་མཆིམས་ཕུར་སྒྲུབ་པ་མཛད༔ རྐྱེན་ངན་ཟློག་ཅིང་དངོས་གྲུབ་གནང་༔ རྗེ་བློན་ཐར་པའི་ལམ་ལ་བཀོད༔ གདོན་གཟུགས་བོན་གྱི་བསྟན་པ་བསྣུབས༔ ཆོས་སྐུ་དྲི་མེད་རིན་ཆེན་བསྟན༔ སྐལ་ལྡན་སངས་རྒྱས་ས་ལ་བཀོད༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ དེ་ནས་ཨོ་རྒྱན་ཡུལ་དུ་བྱོན༔ ད་ལྟ་སྲིན་པོའི་ཁ་གནོན་མཛད༔ མི་ལས་ལྷག་གྱུར་ཡ་མཚན་ཆེ༔ སྤྱོད་པ་རྨད་བྱུང་ངོ་མཚར་ཆེ༔ མཐུ་དང་རྫུ་འཕྲུལ་སྟོབས་པོ་ཆེ༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་སྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ སྐུ་གསུང་ཐུགས་ལྡན་འགྲོ་བ་འདྲེན་པའི་དཔལ༔ སྒྲིབ་པ་ཀུན་སྤངས་ཁམས་གསུམ་ས་ལེར་མཁྱེན༔ དངོས་གྲུབ་མཆོག་བརྙེས་བདེ་ཆེན་མཆོག་གི་སྐུ༔ བྱང་ཆུབ་སྒྲུབ་པའི་བར་ཆད་ངེས་པར་སེལ༔ ཐུགས་རྗེས་བདག་ལ་བྱིན་གྱིས་རློབས༔ བརྩེ་བས་བདག་སོགས་ལམ་སྣ་དྲོངས༔ དགོངས་པས་བདག་ལ་དངོས་གྲུབ་རྩོལ༔ ནུས་པས་བདག་སོགས་བར་ཆད་སོལ༔ ཕྱི་ཡི་བར་ཆད་ཕྱི་རུ་སོལ༔ ནང་གི་བར་ཆད་ནང་དུ་སོལ༔ གསང་བའི་བར་ཆད་དབྱིངས་སུ་སོལ༔ གུས་པས་ཕྱག་འཚལ་སྐྱབས་སུ་མཆི༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་སིདྡྷི་ཧཱུྃ༔ ཨོཾ་ཨཱཿཧཱུྃ་བཛྲ་གུ་རུ་པདྨ་ཐོད་ཕྲེང་རྩལ་བཛྲ་ས་མ་ཡ་ཛཿསིདྡྷི་ཕ་ལ་ཧཱུྃ་ཨཱ༔ ཞེས་པའང་རྩོད་བྲལ་དུས་བབས་ཀྱི་སྤྲུལ་པའི་གཏེར་སྟོན་ཆེན་པོ་ཨོ་རྒྱན་མཆོག་གྱུར་བདེ་ཆེན་གླིང་པས་ཟླ་ཉིན་ཁ་ལ་རོང་སྒོའི་དཔལ་ཆེན་པོའི་ཞབས་འོག་ནས་སྤྱན་དྲངས་པའི་བླ་མའི་ཐུགས་སྒྲུབ་བར་ཆད་ཀུན་སེལ་གྱི་ཞལ་གདམས་སྙིང་བྱང་ཡིད་བཞིན་ནོར་བུ་ལས། ཕྱི་གསོལ་འདེབས་ཀྱི་སྒྲུབ་པ་ཁོལ་དུ་ཕྱུངས་པ་སྟེ། འདིས་ཀྱང་བསྟན་འགྲོའི་བར་ཆད་དང་རྒུད་པ་ཐམས་ཅད་ཉེ་བར་ཞི་ཞིང་དགེ་ལེགས་ཀྱི་དོན་ཐམས་ཅད་ཡོངས་སུ་གྲུབ་པའི་རྒྱུར་གྱུར་ཅིག། ";
         static let SAMPA_LUNDUP_CONTENT: String = "བསམ་པ་ལྷུན་འགྲུབ་མ་བཞུགས་སོ།། ཨེ་མ་ཧོ།། ནུབ་ཕྱོགས་བདེ་བ་ཅན་གྱི་ཞིང་ཁམས་སུ།། སྣང་བ་མཐའ་ཡས་ཐུགས་རྗེའི་བྱིན་བརླབས་ཏེ།། སྤྲུལ་སྐུ་པདྨ་འབྱུང་གནས་བྱིན་བརླབས་ཏེ།། འཛམ་བུ་གླིང་དུ་འགྲོ་བའི་དོན་ལ་བྱོན།། འགྲོ་དོན་རྒྱུན་ཆད་མེད་པའི་ཐུགས་རྗེ་ཅན།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། རྒྱལ་པོ་ཁྲི་སྲོང་ལྡེའུ་བཙན་མན་ཆད་ནས།། ཆོས་རྒྱལ་གདུང་བརྒྱུད་མཐའ་ནས་མ་སྟོངས་པར།། དུས་གསུམ་རྒྱུན་ཆད་མེད་པར་བྱིན་གྱིས་རློབས།། བོད་ཀྱི་ཆོས་སྐྱོང་རྒྱལ་པོའི་གཉེན་གཅིག་པུ།། རྒྱལ་པོ་ཆོས་སྤྱོོད་སྐྱོང་བའི་ཐུགས་རྗེ་ཅན།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། སྐུ་ནི་ལྷོ་ནུབ་སྲིན་པོའི་ཁ་གནོན་མཛད།། ཐུགས་རྗེས་བོད་ཀྱི་སེམས་ཅན་དོན་ལ་དགོངས།། མ་རིག་ལོག་པའི་སེམས་ཅན་འདྲེན་པའི་དཔལ།། ཉོན་མོངས་གདུལ་དཀའི་སེམས་ཅན་ཐབས་ཀྱིས་འདུལ།། བརྩེ་གདུང་རྒྱུན་ཆད་མེད་པའི་ཐུགས་རྗེ་ཅན།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། དུས་ངན་སྙིགས་མའི་མཐའ་ལ་ཐུག་པའི་ཚེ།། ནངས་རེ་དགོང་རེ་བོད་ཀྱི་དོན་ལ་འབྱོན།། ཉེ་ཟེར་འཆར་སྡུད་མདངས་ལ་བཅིབས་ཏེ་འབྱོན།། ཡང་ངོ་ཚེས་བཅུའི་དུས་སུ་དངོས་སུ་འབྱོན།། འགྲོ་དོན་སྟོབས་ཆེན་མཛད་པའི་ཐུགས་རྗེ་ཅན།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། ལྔ་བརྒྱ་ཐ་མ་རྩོད་དུས་སྙིགས་མ་ལ།། སེམས་ཅན་ཐམས་ཅད་ཉོན་མོངས་དུག་ལྔ་རགས།། ཉོན་མོངས་འཇོལ་ཉོག་དུག་ལྔ་རང་རྒྱུད་སྤྱོད།། དེ་འདྲའི་དུས་ན་ཨོ་རྒྱན་ཐུགས་རྗེ་བསྐྱེད།། དད་ལྡན་མཐོ་རིས་འདྲེན་པའི་ཐུགས་རྗེ་ཅན།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། ཧོར་སོགས་འཇིགས་པའི་དམག་གིས་མཐའ་བསྐོར་ནས།། ཆོས་འཁོར་གཉན་པོ་འཇིག་ལ་ཐུག་པའི་ཚེ།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་འདེབས།། ཨོ་རྒྱན་ལྷ་སྲིན་སྡེ་བརྒྱད་འཁོར་དང་བཅས།། དམག་དཔུང་ཧུར་ཐུམ་བཟློག་པར་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། སེམས་ཅན་སྒྱུ་ལུས་འཇིག་པའི་ནད་བྱུང་ནས།། མི་བཟོད་སྡུག་བསྔལ་ཆེན་པོས་ནོན་པའི་ཚེ།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་འདེབས།། ཨོ་རྒྱན་སྨན་གྱི་བླ་དང་དབྱེར་མེད་པས།། ཚེ་ཟད་མ་ཡིན་བར་ཆད་ངེས་པར་སེལ།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། འབྱུང་བ་དགྲར་ལངས་ས་བཅུད་ཉམས་པའི་ཚེ།། སེམས་ཅན་མུ་གེའི་ནད་ཀྱིས་ཉེན་པ་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་ཐོབས།། ཨོ་རྒྱན་མཁའ་འགྲོ་ནོར་ལྷའི་ཚོགས་དང་བཅས།། དབུལ་ཕོངས་བཀྲེས་སྐོམ་སེལ་བར་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། ལས་ཅན་འགྲོ་བའི་དོན་དུ་གཏེར་འདོན་ན།། དམ་ཚིིག་ཟོལ་ཟོག་མེད་པའི་དཔའ་གདེང་གིས།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་འདེབས།། ཨོ་རྒྱན་ཡི་དམ་ལྷ་དང་དབྱེར་མེད་པས།། ཕ་ནོར་བུ་ཡིས་ལོངས་པར་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། སྦས་ཡུལ་ནགས་རོང་དབེན་ས་སྙོག་པའི་ཚེ།། ཁ་ཆར་བུ་ཡུག་འཚུབས་ཤིང་ལམ་འགགས་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་འདེབས།། ཨོ་རྒྱན་གཞི་བདག་གཉན་པོའི་འཁོར་དང་བཅས།། ཆོས་མཛད་ལམ་སྣ་འདྲེན་པར་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། སྟག་གཟིག་དོམ་དྲེད་དུག་སྦྲུལ་མཆེ་བ་ཅན།། འབྲོག་ཆེན་འཇིགས་པའི་འཕྲང་ལ་འགྲིམ་པའི་ཚེ།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་ཐོབས།། ཨོ་རྒྱན་དཔའ་བོ་གིང་དང་སྲུང་མར་བཅས།། གདུག་པའི་སེམས་ཅན་སྐྲོད་པར་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། ས་ཆུ་མེ་རླུང་འབྱུང་བའི་བར་ཆད་ཀྱིས།། སྒྱུ་ལུས་གཡར་པོ་འཇིག་ལ་ཐུག་པའི་ཚེ།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་ཐོབས།། ཨོ་རྒྱན་དཔའ་བོ་གྱད་དང་ལྡན་པ་ཡིས།། འབྱུང་བ་རང་སར་ཞི་བ་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། ལམ་སྲང་འཇིགས་པའི་འཕྲང་ལ་འགྲིམ་པའི་ཚེ།། བསད་ཁྱེར་ཇག་པ་ཆོམ་པོས་ཉེན་པ་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་ཐོབས།། ཨོ་རྒྱན་ཕྱག་རྒྱ་བཞི་ཡི་དགོངས་པར་ལྡན།། ཙོ་ར་མི་རྒོད་རྔམས་སེམས་བརླག་པར་བྱེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། གང་ཞིག་གཤེད་མའི་དམག་གིས་མཐའ་བསྐོར་ནས།། མཚོན་ཆ་རྣོན་པོ་འདེབས་ཤིང་ཉེན་པ་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་ཐོབས།། ཨོ་རྒྱན་རྡོ་རྗེའི་གུར་དང་ལྡན་པ་ཡིས།། གཤེད་མ་བྲེད་ཅིང་མཚོན་ཆ་འཐོར་བར་འགྱུར།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། ནམ་ཞིག་ཚེ་ཟད་འཆི་བའི་དུས་བྱུང་ཚེ།། གནད་གཅོད་སྡུག་བསྔལ་འདུ་འཕྲོད་ཉེན་པ་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་འདེབས།། ཨོ་རྒྱན་སྣང་བ་མཐའ་ཡས་སྤྲུལ་པ་སྟེ།། བདེ་བ་ཅན་གྱི་ཞིང་དུ་ངེས་པར་སྐྱེ།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། སྒྱུ་ལུས་གཡར་པོ་ཞིག་པའི་བར་དོ་རུ།། འཁྲུལ་སྣང་ཉིང་འཁྲུལ་ཆེན་པོས་ཉེན་པ་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་འདེབས།། ཨོ་རྒྱན་དུས་གསུམ་འཁྱེན་པའི་ཐུགས་རྗེ་ཡིས།། འཁྲུལ་སྣང་རང་སར་གྲོལ་བ་ཐེ་ཚོམ་མེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། གཞན་ཡང་ལས་དང་རྐྱེན་གྱི་དབང་གྱུར་ཏེ།། འཁྲུལ་སྣང་དངོས་པོར་ཞེན་ཅིང་སྡུག་བསྔལ་ན།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་ཐོབས།། ཨོ་རྒྱན་བདེ་ཆེན་རྒྱལ་པོའ་ངོ་བོ་སྟེ།། འཁྲུལ་སྣང་སྡུལ་བསྔལ་རྩད་ནས་བཤིག་པར་བྱེད།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།། འགྲོ་དྲུག་སྡུག་བསྔལ་ཆེན་པོས་ཉེན་པ་དང་།། ཁྱད་པར་བོད་ཀྱི་རྗེ་འབངས་སྡུག་བསྔལ་ན།། དད་གུས་མོས་པའི་གདུང་ཤུགས་དྲག་པོ་ཡིས།། ཡིད་གཉིས་ཐེ་ཚོམ་མེད་པར་གསོལ་བ་འདེབས།། ཨོ་རྒྱན་ཐུགས་རྗེ་འཕོ་འགྱུར་མེད་པར་གཟིགས།། ཨོ་རྒྱན་པདྨ་འབྱུང་གནས་ལ་གསོལ་བ་འདེབས།། བསམ་པ་ལྷུན་གྱིས་འགྲུབ་པར་བྱིན་གྱིས་རློབས།";
